import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div
      style={{
        position: 'relative',
        border: '1px solid #ccc',
        borderRadius: 8,
        padding: 16,
        margin: 8,
        width: 280,
        textAlign: 'center',
        userSelect: 'none',
      }}
    >
      {/* Info desplegada arriba, solo si showInfo es true */}
      {showInfo && (
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            backgroundColor: 'rgba(0,0,0,0.75)',
            color: 'white',
            padding: '10px',
            borderTopLeftRadius: 8,
            borderTopRightRadius: 8,
            zIndex: 10,
            fontSize: 13,
            lineHeight: '1.3',
          }}
        >
          <strong>{nombre}</strong>
          <p style={{ margin: '6px 0' }}>{descripcion}</p>
          <p>Precio: ${precio}</p>
          {colores && <p>Colores: {colores.join(', ')}</p>}
          {tallas && <p>Tallas: {tallas.join(', ')}</p>}
        </div>
      )}

      {/* Imagen con clic para mostrar/ocultar info */}
      <img
        src={imagen}
        alt={nombre}
        style={{
          width: '100%',
          borderRadius: 8,
          cursor: 'pointer',
          marginTop: showInfo ? 100 : 0, // espacio para info arriba
          transition: 'margin-top 0.3s ease',
        }}
        onClick={() => setShowInfo(!showInfo)}
      />

      {/* Bot√≥n fijo abajo */}
      <button
        onClick={onAgregar}
        style={{
          marginTop: 12,
          padding: '10px 16px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: 6,
          cursor: 'pointer',
          width: '100%',
        }}
      >
        Agregar al carrito
      </button>
    </div>
  );
};

export default Tarjeta;
