// LogicaCampoFormulario.js
import React from 'react';

const LogicaCampoFormulario = ({ name, label, type, options, formData, setFormData, accionActiva }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const isDisabled = accionActiva === 'Dar de Baja';
  const isRequired = !['Dar de Baja', 'Buscar', 'Modificar'].includes(accionActiva);
  const isFechaEntrega = name === 'fecha_entrega';

  if (type === 'select') {
    return (
      <div className="campo">
        <label htmlFor={name}>{label}:</label>
        <select
          id={name}
          name={name}
          value={formData[name] || ''}
          onChange={handleChange}
          disabled={isDisabled}
          required={isRequired}
        >
          <option value="">Seleccione...</option>
          {options.map(opt => (
            <option key={opt} value={opt}>{opt}</option>
          ))}
        </select>
      </div>
    );
  }

  if (type === 'textarea') {
    return (
      <div className="campo">
        <label htmlFor={name}>{label}:</label>
        <textarea
          id={name}
          name={name}
          value={formData[name] || ''}
          onChange={handleChange}
          rows={4}
          disabled={isDisabled}
          required={isRequired}
        />
      </div>
    );
  }

  return (
    <div className="campo">
      <label htmlFor={name}>{label}:</label>
      <input
        id={name}
        name={name}
        type={type}
        value={formData[name] || ''}
        onChange={handleChange}
        disabled={isDisabled}
        required={isFechaEntrega ? false : isRequired}
      />
    </div>
  );
};

export default LogicaCampoFormulario;
