import React, { useState, useRef, useEffect } from 'react';
import '../css/agente.css'; // Usaremos el CSS que ya existe en el proyecto
import avatar from '../img/logo.jpg'; // Importamos el avatar

const Agente = ({ onSelectCategory, categorias = [] }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  
  const initialMessage = { 
    from: 'bot', 
    text: 'Â¡Hola! Soy Daniel, tu asesor de moda personal. Estoy aquÃ­ para ayudarte a encontrar justo lo que necesitas. Â¿Empezamos?', 
    options: ['Ver Productos', 'Medios de Pago', 'Horarios de AtenciÃ³n'] 
  };

  useEffect(() => {
    setMessages([initialMessage]);
  }, []);

  const chatBodyRef = useRef(null);

  // Efecto para hacer scroll hacia abajo cada vez que se aÃ±ade un mensaje
  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
  }, [messages]);

  const toggleChat = () => {
    setIsOpen(!isOpen);
    if (!isOpen && messages.length === 0) {
      setMessages([initialMessage]);
    }
  };

  const handleOptionClick = (option) => {
    const userMessage = { from: 'user', text: option };
    let botResponse;

    if (categorias.includes(option)) {
      onSelectCategory(option);
      botResponse = { from: 'bot', text: `Â¡Excelente elecciÃ³n! Ya puedes ver todos los productos de ${option}. Â¿Hay algo mÃ¡s en lo que pueda asesorarte?`, options: initialMessage.options };
    } else {
      switch (option) {
        case 'Ver Productos':
          botResponse = { from: 'bot', text: 'Â¡Perfecto! Nuestras colecciones son increÃ­bles. Â¿CuÃ¡l de estas categorÃ­as te gustarÃ­a explorar?', options: [...categorias, 'Volver al menÃº principal'] };
          break;
        case 'Medios de Pago':
          botResponse = { from: 'bot', text: 'Para tu comodidad, aceptamos tarjetas de crÃ©dito, dÃ©bito y transferencias por Nequi y Daviplata. Â¡Comprar es muy fÃ¡cil!', options: ['Volver al menÃº principal'] };
          break;
        case 'Horarios de AtenciÃ³n':
          botResponse = { from: 'bot', text: 'Atendemos de Lunes a SÃ¡bado, de 9:00 AM a 6:00 PM. Â¡SerÃ¡ un placer ayudarte!', options: ['Volver al menÃº principal'] };
          break;
        case 'Volver al menÃº principal':
          botResponse = initialMessage;
          break;
        default:
          botResponse = { from: 'bot', text: 'No estoy seguro de entenderte. Â¿QuÃ© te parece si eliges una de estas opciones para continuar?', options: initialMessage.options };
          break;
      }
    }

    // Para "Volver al menÃº principal", reemplazamos los mensajes para no alargar la conversaciÃ³n
    if (option === 'Volver al menÃº principal') {
      setMessages([userMessage, botResponse]);
    } else {
      setMessages([...messages, userMessage, botResponse]);
    }
  };

  const currentOptions = messages[messages.length - 1]?.options || [];

  return (
    <div className="chatbot-container">
      <div className={`chat-window ${isOpen ? 'open' : ''}`}>
        <div className="chat-header">
          <img src={avatar} alt="Avatar de Daniel" className="chat-avatar" />
          <h2>Daniel, tu Asesor</h2>
          <button onClick={toggleChat} className="close-btn">&times;</button>
        </div>
        <div className="chat-body" ref={chatBodyRef}>
          {messages.map((msg, index) => (
            <div key={index} className={`message-container ${msg.from}`}>
              <div className={`message ${msg.from}`}>
                {msg.text}
              </div>
            </div>
          ))}
        </div>
        <div className="chat-footer">
          {currentOptions.map((option, index) => (
            <button key={index} onClick={() => handleOptionClick(option)} className="option-btn">
              {option}
            </button>
          ))}
        </div>
      </div>
      <button onClick={toggleChat} className="chat-toggle-btn">
        <span>ðŸ’¬</span>
      </button>
    </div>
  );
};

export default Agente;
