import React, { useState, useEffect } from 'react';
import CreatableSelect from 'react-select/creatable';

const modulos = ['Inventario', 'Productos', 'Proveedores', 'Usuarios', 'Ventas'];
const acciones = ['Agregar', 'Modificar', 'Buscar', 'Dar de baja / Habilitar', 'Ver'];

const camposPorModulo = {
  Inventario: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre del Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'numero_factura_orden', label: 'Número Factura u Orden', type: 'text' },
    { name: 'ubicacion', label: 'Ubicación', type: 'text' },
    { name: 'custodia', label: 'Custodia', type: 'select', options: ['Área A', 'Área B', 'Área C'] },
    { name: 'proveedor', label: 'Proveedor', type: 'select', options: ['Proveedor 1', 'Proveedor 2', 'Proveedor 3'] },
    { name: 'fecha_ingreso', label: 'Fecha de Ingreso', type: 'datetime-local' },
    { name: 'fecha_entrega', label: 'Fecha de Entrega', type: 'datetime-local' },
    { name: 'tipo_movimiento', label: 'Tipo de Movimiento', type: 'creatable-select' },
    { name: 'orden_salida', label: 'Orden de Salida', type: 'text' },
    { name: 'descripcion', label: 'Descripción', type: 'textarea' },
  ],
  Productos: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre', type: 'text' },
    { name: 'categoria', label: 'Categoría', type: 'text' },
    { name: 'precio', label: 'Precio', type: 'number' },
  ],
  Proveedores: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre', type: 'text' },
    { name: 'telefono', label: 'Teléfono', type: 'text' },
    { name: 'email', label: 'Email', type: 'email' },
  ],
  Usuarios: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'usuario', label: 'Usuario', type: 'text' },
    { name: 'email', label: 'Email', type: 'email' },
    { name: 'rol', label: 'Rol', type: 'text' },
  ],
  Ventas: [
    { name: 'id', label: 'ID Venta', type: 'number' },
    { name: 'producto', label: 'Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'total', label: 'Total', type: 'number' },
  ],
};

const PanelAdmin = () => {
  const [moduloActivo, setModuloActivo] = useState(modulos[0]);
  const [accionActiva, setAccionActiva] = useState(null);
  const [formData, setFormData] = useState({});
  const [creatableValue, setCreatableValue] = useState(null);

  useEffect(() => {
    setFormData({});
    setCreatableValue(null);
  }, [moduloActivo, accionActiva]);

  const campos = camposPorModulo[moduloActivo] || [];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleCreatableChange = (selectedOption, name) => {
    setCreatableValue(selectedOption);
    setFormData(prev => ({
      ...prev,
      [name]: selectedOption ? selectedOption.value : '',
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Acción: ${accionActiva}\nMódulo: ${moduloActivo}\nDatos: ${JSON.stringify(formData, null, 2)}`);
  };

  const esVer = accionActiva === 'Ver';
  const esDesactivar = accionActiva === 'Dar de baja / Habilitar';

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial', maxWidth: 700 }}>
      <h1>Panel de Administración</h1>

      <div style={{ marginBottom: '1rem' }}>
        {modulos.map(m => (
          <button
            key={m}
            onClick={() => { setModuloActivo(m); setAccionActiva(null); }}
            style={{
              marginRight: 8,
              padding: '0.5rem 1rem',
              backgroundColor: m === moduloActivo ? '#007bff' : '#eee',
              color: m === moduloActivo ? 'white' : 'black',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer',
            }}
          >
            {m}
          </button>
        ))}
      </div>

      <div style={{ marginBottom: '1rem' }}>
        {acciones.map(a => (
          <button
            key={a}
            onClick={() => setAccionActiva(a)}
            style={{
              marginRight: 8,
              padding: '0.4rem 0.8rem',
              backgroundColor: a === accionActiva ? '#28a745' : '#ddd',
              color: a === accionActiva ? 'white' : 'black',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer',
            }}
          >
            {a}
          </button>
        ))}
      </div>

      <div style={{ border: '1px solid #ccc', padding: 16, borderRadius: 6 }}>
        {!accionActiva && (
          <p>Selecciona una acción para el módulo <b>{moduloActivo}</b>.</p>
        )}

        {esVer && (
          <div>
            <h3>Datos del módulo <b>{moduloActivo}</b></h3>
            <p>Aquí se mostrarían los datos para visualizar.</p>
          </div>
        )}

        {accionActiva && !esVer && (
          <form onSubmit={handleSubmit}>
            <h3>{accionActiva} - {moduloActivo}</h3>

            {campos.map(({ name, label, type, options }) => {
              const soloID = esDesactivar && name !== 'id';
              const requerido = accionActiva !== 'Buscar' && name === 'id';

              if (type === 'select') {
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name}>{label}:</label>
                    <select
                      id={name}
                      name={name}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      required={requerido}
                      disabled={soloID}
                      style={{ width: '100%', padding: '0.4rem', borderRadius: 4, border: '1px solid #ccc' }}
                    >
                      <option value="">Seleccione...</option>
                      {options.map(opt => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </div>
                );
              } else if (type === 'creatable-select') {
                const opcionesMovimiento = [
                  { value: 'Ingreso de Mercancía', label: 'Ingreso de Mercancía' },
                  { value: 'Traslado', label: 'Traslado de Mercancía' },
                  { value: 'Devolución', label: 'Devolución de Mercancía' },
                  { value: 'Garantía', label: 'Garantía de Mercancía' },
                ];
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label>{label}:</label>
                    <CreatableSelect
                      isClearable
                      options={opcionesMovimiento}
                      onChange={(selected) => handleCreatableChange(selected, name)}
                      value={creatableValue}
                      isDisabled={soloID}
                      placeholder="Seleccione o escriba una opción"
                    />
                  </div>
                );
              } else if (type === 'textarea') {
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name}>{label}:</label>
                    <textarea
                      id={name}
                      name={name}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      rows={4}
                      disabled={soloID}
                      required={requerido}
                      style={{ width: '100%', padding: '0.5rem', borderRadius: 4, border: '1px solid #ccc' }}
                    />
                  </div>
                );
              } else {
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name}>{label}:</label>
                    <input
                      id={name}
                      name={name}
                      type={type}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      disabled={soloID}
                      required={requerido}
                      style={{ width: '100%', padding: '0.4rem', borderRadius: 4, border: '1px solid #ccc' }}
                    />
                  </div>
                );
              }
            })}

            <button
              type="submit"
              style={{
                backgroundColor: '#007bff',
                color: 'white',
                padding: '0.6rem 1.2rem',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              Enviar
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default PanelAdmin;
