import React, { useState } from 'react';
import Tarjeta from '../elementos/Tarjeta';
import { productos } from './productos';

const Catalogo = () => {
  const [carrito, setCarrito] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState(null);
  const [hoverMenu, setHoverMenu] = useState(false);

  const handleAgregarAlCarrito = (producto) => {
    setCarrito((prev) => [...prev, producto]);
  };

  const productosFiltrados = categoriaSeleccionada
    ? productos.filter((p) => p.categoria === categoriaSeleccionada)
    : productos;

  const subcategorias = [
    'Boxer sin costuras',
    'Boxer sin costura',
    'Boxer microfibra deportivo',
    'Boxer algodón premium',
    'Boxer mezcla stretch',
    'Boxer compresión ligera',
  ];

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      
      {/* Menú lateral */}
      <div style={{ width: '20%', padding: '20px', borderRight: '1px solid #ccc' }}>
        <div
          onMouseEnter={() => setHoverMenu(true)}
          onMouseLeave={() => setHoverMenu(false)}
          style={{ position: 'relative' }}
        >
          <div style={{ fontWeight: 'bold', cursor: 'pointer' }}>Boxers ▸</div>
          {hoverMenu && (
            <ul
              style={{
                listStyle: 'none',
                padding: '10px',
                margin: 0,
                position: 'absolute',
                left: '100%',
                top: 0,
                backgroundColor: '#fff',
                border: '1px solid #ccc',
                boxShadow: '2px 2px 6px rgba(0,0,0,0.1)',
              }}
            >
              {subcategorias.map((cat) => (
                <li
                  key={cat}
                  onClick={() => setCategoriaSeleccionada(cat)}
                  style={{
                    padding: '5px 10px',
                    cursor: 'pointer',
                    whiteSpace: 'nowrap',
                  }}
                >
                  {cat}
                </li>
              ))}
            </ul>
          )}
        </div>
        <div
          style={{
            marginTop: '20px',
            color: 'blue',
            cursor: 'pointer',
          }}
          onClick={() => setCategoriaSeleccionada(null)}
        >
          Mostrar todos
        </div>
      </div>

      {/* Catálogo */}
      <div style={{ width: '60%', padding: '20px', overflowY: 'auto' }}>
        <h2>{categoriaSeleccionada || 'Todos los productos'}</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px' }}>
          {productosFiltrados.map((producto) => (
            <Tarjeta
              key={producto.id}
              imagen={producto.imagen}
              nombre={producto.nombre}
              descripcion={producto.descripcion}
              precio={producto.precio}
              colores={producto.colores}
              tallas={producto.tallas}
              onAgregar={() => handleAgregarAlCarrito(producto)}
            />
          ))}
        </div>
      </div>

      {/* Carrito */}
      <div style={{ width: '20%', padding: '20px', borderLeft: '1px solid #ccc', background: '#f9f9f9' }}>
        <h3>Carrito</h3>
        <ul>
          {carrito.map((item, index) => (
            <li key={index}>
              {item.nombre} - ${item.precio}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Catalogo;
