import React from 'react';
import '../css/vistaaInventario.css';
import LogicaCampoFormulario from '../logica/LogicaCampoFormulario';

const VistaAInventario = ({ accionActiva, formData, setFormData }) => {
  const campos = [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre del Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'numero_factura_orden', label: 'Número Factura u Orden', type: 'text' },
    { name: 'ubicacion', label: 'Ubicación', type: 'text' },
    {
      name: 'custodia',
      label: 'Custodia',
      type: 'select',
      options: ['Área A', 'Área B', 'Área C'],
    },
    {
      name: 'proveedor',
      label: 'Proveedor',
      type: 'select',
      options: ['Proveedor 1', 'Proveedor 2', 'Proveedor 3'],
    },
    { name: 'fecha_ingreso', label: 'Fecha de Ingreso', type: 'datetime-local' },
    { name: 'fecha_entrega', label: 'Fecha de Entrega', type: 'datetime-local', optional: true },
    { name: 'descripcion', label: 'Descripción', type: 'textarea' },
  ];

  return (
    <div>
      {campos.map((campo) => (
        <LogicaCampoFormulario
          key={campo.name}
          campo={campo}
          formData={formData}
          setFormData={setFormData}
          accionActiva={accionActiva}
        />
      ))}
    </div>
  );
};

export default VistaAInventario;
