// src/componentes/elementos/Pedido.js
import React, { useState } from 'react';
import './../css/pedido.css'; // Importamos el nuevo CSS

const ElementoPedido = ({ items, vaciarPedido }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    telefono: '',
    direccion: '',
    email: ''
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleComprar = (e) => {
    e.preventDefault();
    
    if (items.length === 0) {
      alert('Tu pedido está vacío. Añade productos antes de realizar la compra.');
      return;
    }

    // Crear mensaje de WhatsApp
    const mensaje = `Hola! Quiero hacer un pedido:\n\n` +
      items.map(item => 
        `• ${item.nombre} - Color: ${item.color} - Talla: ${item.talla} - Cantidad: ${item.cantidad} - $${item.precio}`
      ).join('\n') +
      `\n\nTotal: $${items.reduce((total, item) => total + (item.precio * item.cantidad), 0)}` +
      `\n\nDatos de contacto:` +
      `\nNombre: ${formData.nombre}` +
      `\nTeléfono: ${formData.telefono}` +
      `\nDirección: ${formData.direccion}` +
      `\nEmail: ${formData.email}`;

    // Codificar el mensaje para WhatsApp
    const mensajeCodificado = encodeURIComponent(mensaje);
    const urlWhatsApp = `https://wa.me/57321798086?text=${mensajeCodificado}`;

    // Abrir WhatsApp
    window.open(urlWhatsApp, '_blank');

    // Opcional: limpiar pedido y formulario después de enviar
    vaciarPedido();
    setFormData({ nombre: '', telefono: '', direccion: '', email: '' });
  };

  return (
    <div className="catalogo-pedido">
      <h3>Pedido</h3>
      
      {items.length === 0 ? (
        <p>El pedido está vacío.</p>
      ) : (
        <div>
          <ul>
            {items.map((item, index) => (
              <li key={index}>
                <strong>{item.nombre}</strong> - {item.color} - Talla: {item.talla} - Cantidad: {item.cantidad} - ${item.precio}
              </li>
            ))}
          </ul>
          
          <p><strong>Total: ${items.reduce((total, item) => total + (item.precio * item.cantidad), 0)}</strong></p>
        </div>
      )}

      <form onSubmit={handleComprar} className="pedido-form">
        <h4>Datos de contacto</h4>
        
        <input
          type="text"
          name="nombre"
          placeholder="Nombre completo"
          value={formData.nombre}
          onChange={handleInputChange}
          required
        />
        
        <input
          type="tel"
          name="telefono"
          placeholder="Teléfono"
          value={formData.telefono}
          onChange={handleInputChange}
          required
        />
        
        <input
          type="text"
          name="direccion"
          placeholder="Dirección de entrega"
          value={formData.direccion}
          onChange={handleInputChange}
          required
        />
        
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleInputChange}
          required
        />
        
        <button type="submit" disabled={items.length === 0}>
          Realizar Pedido por WhatsApp
        </button>
      </form>
    </div>
  );
};

export default ElementoPedido;
