import React, { useState } from 'react';
import CreatableSelect from 'react-select/creatable';

const PanelAdmin = () => {
  // ... tus estados y demás

  // Para campos tipo creatable-select, manejar valor separado
  const [creatableValue, setCreatableValue] = useState(null);

  const campos = camposPorModulo[moduloActivo] || [];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  // Maneja cambio de creatable-select
  const handleCreatableChange = (selectedOption, name) => {
    setCreatableValue(selectedOption);
    setFormData(prev => ({
      ...prev,
      [name]: selectedOption ? selectedOption.value : '',
    }));
  };

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial, sans-serif', maxWidth: 600 }}>
      {/* ...botones módulos y acciones */}

      <div style={{ border: '1px solid #ccc', padding: 16, borderRadius: 6 }}>
        {!accionActiva && (
          <p>Selecciona una acción para el módulo <b>{moduloActivo}</b>.</p>
        )}

        {(accionActiva === 'Ver') && (
          <div>
            <h3>Datos del módulo <b>{moduloActivo}</b></h3>
            <p>Aquí se mostrarían los datos para visualizar.</p>
          </div>
        )}

        {(accionActiva && accionActiva !== 'Ver') && (
          <form onSubmit={handleSubmit}>
            <h3>{accionActiva} - {moduloActivo}</h3>
            {campos.map(({ name, label, type, options }) => {
              if (type === 'select') {
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                      {label}:
                    </label>
                    <select
                      id={name}
                      name={name}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                      disabled={accionActiva === 'Dar de baja'}
                      style={{
                        width: '100%',
                        padding: '0.4rem',
                        borderRadius: 4,
                        border: '1px solid #ccc',
                      }}
                    >
                      <option value="">Seleccione...</option>
                      {options && options.map(opt => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </div>
                );
              } else if (type === 'creatable-select') {
                const opcionesMovimiento = [
                  { value: 'Ingreso de Mercancía', label: 'Ingreso de Mercancía' },
                  { value: 'Traslado', label: 'Traslado' },
                  { value: 'Devolución', label: 'Devolución' },
                  { value: 'Garantía', label: 'Garantía' },
                ];
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label style={{ display: 'block', marginBottom: 4 }}>{label}:</label>
                    <CreatableSelect
                      isClearable
                      options={opcionesMovimiento}
                      onChange={(selected) => handleCreatableChange(selected, name)}
                      value={creatableValue}
                      isDisabled={accionActiva === 'Dar de baja'}
                      placeholder="Seleccione o escriba una opción"
                    />
                  </div>
                );
              } else if (type === 'textarea') {
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                      {label}:
                    </label>
                    <textarea
                      id={name}
                      name={name}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                      disabled={accionActiva === 'Dar de baja'}
                      style={{
                        width: '100%',
                        padding: '0.4rem',
                        borderRadius: 4,
                        border: '1px solid #ccc',
                        minHeight: 80,
                      }}
                    />
                  </div>
                );
              } else {
                // Inputs tipo text, number, datetime-local, email, etc.
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                      {label}:
                    </label>
                    <input
                      id={name}
                      name={name}
                      type={type}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                      disabled={accionActiva === 'Dar de baja'}
                      style={{
                        width: '100%',
                        padding: '0.4rem',
                        borderRadius: 4,
                        border: '1px solid #ccc',
                      }}
                    />
                  </div>
                );
              }
            })}

            <button
              type="submit"
              style={{
                backgroundColor: '#007bff',
                color: 'white',
                padding: '0.5rem 1rem',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              Ejecutar
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default PanelAdmin;
