// src/logica/usuarios.js

const usuariosPorDefecto = [
  { id: 1, usuario: 'sandra', password: '123', rol: 'cliente' },
  { id: 2, usuario: 'sergio', password: '12345', rol: 'proveedor' },
  { id: 3, usuario: 'andres', password: '1234abcd', rol: 'admin' },
];

export const obtenerUsuarios = () => {
  const guardados = localStorage.getItem('usuarios');
  return guardados ? JSON.parse(guardados) : usuariosPorDefecto;
};

export const guardarUsuarios = (usuarios) => {
  localStorage.setItem('usuarios', JSON.stringify(usuarios));
};
