// Archivo: LogicaCampoFormulario.js

const camposBase = [
  // ... tus campos
];

export function obtenerCamposPorAccion(accion) {
  let campos = [...camposBase];

  if (accion === 'Entradas') {
    campos = campos.filter(c => c.name !== 'fecha_entrega');
    campos = campos.filter(c => c.name !== 'fecha_ingreso' || c.name === 'tipo_movimiento');
  } else if (accion === 'Salidas') {
    campos = campos.filter(c => c.name !== 'fecha_ingreso');
  } else if (accion === 'Modificar' || accion === 'Buscar') {
    campos = campos.map(c => ({ ...c, required: false }));
  } else if (accion === 'Dar de Baja' || accion === 'Habilitar') {
    campos.push({ name: 'estado', label: 'Estado', type: 'select', options: ['activo', 'dar de baja'] });
  }

  if (accion === 'Ver') {
    return [];
  }

  return campos;
}
