import React, { useState } from 'react';
import './App.css'; // Asumo que quieres mantener los estilos base

// Componentes principales de la aplicación
import Carrucel from './componentes/elementos/ElementoCarrucel.js';
import Catalogo from './componentes/vistas/VistaACatalogo.js';
import Footer from './componentes/elementos/ElementoFooter.js';
import ElementoNavProductos from './componentes/elementos/ElementoNavProductos.js';
import Agente from './componentes/elementos/Agente';
import { ElementoProductos } from './componentes/elementos/ElementoProductos';

// Componentes de terceros
import ChatBotComponent from 'react-chatbotify';

function App() {
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('Todos');
  const [carrito, setCarrito] = useState([]);

  const handleAgregarAlCarrito = (productoAgregado) => {
    setCarrito((prev) => {
      // Un producto es único por su ID y su color
      const productoExistente = prev.find(
        (item) => item.id === productoAgregado.id && item.color === productoAgregado.color
      );

      if (productoExistente) {
        // Si ya existe, actualizamos su cantidad
        return prev.map((item) =>
          item.id === productoAgregado.id && item.color === productoAgregado.color
            ? { ...item, cantidad: item.cantidad + productoAgregado.cantidad }
            : item
        );
      } else {
        // Si no existe, lo añadimos al carrito
        return [...prev, productoAgregado];
      }
    });
    alert(`Se agregaron ${productoAgregado.cantidad} "${productoAgregado.nombre}" de color ${productoAgregado.color} al carrito.`);
  };

  const vaciarCarrito = () => {
    setCarrito([]);
  };

  const categorias = ['Todos', ...new Set(ElementoProductos.map(p => p.category))];

  return (
    <div className="App">
      <header className="App-header">
        <Carrucel />
        <ElementoNavProductos onSelectCategory={setCategoriaSeleccionada} categorias={categorias} />
      </header>

      <main>
        <Catalogo
          categoriaSeleccionada={categoriaSeleccionada}
          carrito={carrito}
          agregarAlCarrito={handleAgregarAlCarrito}
          vaciarCarrito={vaciarCarrito}
        />
      </main>

      <Footer />
      <ChatBotComponent />
      <Agente onSelectCategory={setCategoriaSeleccionada} categorias={categorias} />
    </div>
  );
}

export default App;
