import React, { useState } from 'react';
import Tarjeta from '../elementos/ElementoTarjeta';
import Carrito from '../elementos/ElementoCarrito';
import { ElementoProductos } from '../elementos/ElementoProductos';
import ElementoTitulo from '../elementos/ElementoTitulo';
import '../css/catalogo.css';

const VistaACatalogo = ({ categoriaSeleccionada }) => {
  const [carrito, setCarrito] = useState([]);

  const handleAgregarAlCarrito = (producto) => {
    setCarrito((prev) => {
      // Buscamos si el producto ya está en el carrito
      const productoExistente = prev.find((item) => item.id === producto.id);
      
      if (productoExistente) {
        // Si existe, mapeamos el arreglo y actualizamos la cantidad
        return prev.map((item) =>
          item.id === producto.id
            ? { ...item, cantidad: item.cantidad + 1 }
            : item
        );
      } else {
        // Si no existe, lo agregamos con cantidad 1
        return [...prev, { ...producto, cantidad: 1 }];
      }
    });
  };

  const vaciarCarrito = () => {
    setCarrito([]);
    alert('¡Gracias por tu compra! El carrito ha sido vaciado.');
  };

  const productosFiltrados = categoriaSeleccionada === 'Todos'
    ? ElementoProductos
    : ElementoProductos.filter((p) => p.category === categoriaSeleccionada);

  return (
    <div className="catalogo-container">
      <div className="catalogo-productos">
        <ElementoTitulo texto="Nuestro Catálogo" />
        <h2>{categoriaSeleccionada}</h2>
        <div className="catalogo-grid">
          {productosFiltrados.length > 0 ? (
            productosFiltrados.map((producto) => (
              <Tarjeta
                key={producto.id}
                imagen={producto.imagen}
                nombre={producto.nombre}
                descripcion={producto.descripcion}
                precio={producto.precio}
                colores={producto.colores}
                tallas={producto.tallas}
                onAgregar={() => handleAgregarAlCarrito(producto)}
              />
            ))
          ) : (
            <p>No hay productos en esta categoría.</p>
          )}
        </div>
      </div>

      <Carrito items={carrito} vaciarCarrito={vaciarCarrito} />
    </div>
  );
};

export default VistaACatalogo;
