// src/componentes/elementos/Tarjeta.js
import React, { useState } from 'react';
import '../css/tarjeta/tarjeta.css'; // Importa el CSS desde la nueva ubicación

const ElementoTarjeta = ({ 
  id, 
  imagen, 
  nombre, 
  descripcion, 
  precio, 
  colores = [], 
  tallas = [],
  coloresDisponibles = [],
  tallasDisponibles = [],
  onAgregar 
}) => {
  const [cantidad, setCantidad] = useState(1);
  const [colorSeleccionado, setColorSeleccionado] = useState(null);
  const [tallaSeleccionada, setTallaSeleccionada] = useState(null);

  // Mapa de colores mejorado con todos los colores de la base de datos
  const colorMap = {
    'Negro': '#000000',
    'Blanco': '#FFFFFF',
    'Azul': '#0000FF',
    'Azul Marino': '#000080',
    'Gris': '#808080',
    'Rojo': '#FF0000',
    'Verde': '#008000',
    'Rosado': '#FFC0CB',
    'Rosa': '#FFC0CB',
    'Lila': '#C8A2C8',
    'Morado': '#800080',
    'Amarrillo': '#FFFF00',
    'Amarillo': '#FFFF00',
    'Naranja': '#FFA500',
    'Marrón': '#A52A2A',
    'Bordó': '#800020',
    'Celeste': '#87CEEB'
  };

  // Función para obtener el código de color
  const getColorCode = (colorName) => {
    return colorMap[colorName] || '#ccc';
  };

  const handleAgregar = () => {
    if (!colorSeleccionado) {
      alert('Por favor, selecciona un color antes de agregar el producto.');
      return;
    }
    if (!tallaSeleccionada) {
      alert('Por favor, selecciona una talla antes de agregar el producto.');
      return;
    }
    onAgregar({ 
      id, 
      cantidad, 
      color: colorSeleccionado,
      talla: tallaSeleccionada,
      precio_unitario: precio
    });
  };

  return (
    <div className="tarjeta-contenedorPrincipal">
      {/* Imagen del producto */}
      <div className="tarjeta-contenedorVisual">
        <img src={imagen} alt={nombre} className="tarjeta-imagen" />
      </div>

      {/* Contenedor para la información permanente */}
      <div className="tarjeta-informacionPermanente">
        <h3 className="tarjeta-titulo">{nombre}</h3>
        <p className="tarjeta-descripcion">{descripcion}</p>

        <div className="tarjeta-opciones">
          <p>Color:</p>
          <div className="tarjeta-contenedorOpciones">
            {colores.length > 0 ? (
              colores.map((color) => (
                <div
                  key={color}
                  className={`tarjeta-puntoColor ${colorSeleccionado === color ? 'seleccionado' : ''}`}
                  style={{ 
                    backgroundColor: getColorCode(color),
                    border: colorSeleccionado === color ? '3px solid #00ff41' : '2px solid #333'
                  }}
                  onClick={() => setColorSeleccionado(color)}
                  title={color}
                />
              ))
            ) : (
              <span style={{ color: '#999', fontSize: '0.9rem' }}>No hay colores disponibles</span>
            )}
          </div>
        </div>

        {tallas.length > 0 && (
          <div className="tarjeta-opciones">
            <p>Talla:</p>
            <div className="tarjeta-contenedorOpciones">
              {tallas.map((talla) => (
                <div
                  key={talla}
                  className={`tarjeta-opcionTalla ${tallaSeleccionada === talla ? 'seleccionado' : ''}`}
                  onClick={() => setTallaSeleccionada(talla)}
                  style={{
                    border: tallaSeleccionada === talla ? '2px solid #00ff41' : '1px solid #ccc',
                    backgroundColor: tallaSeleccionada === talla ? '#00ff41' : 'transparent',
                    color: tallaSeleccionada === talla ? '#000' : '#333'
                  }}
                >
                  {talla}
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="tarjeta-opciones">
          <p>Cantidad:</p>
          <div className="tarjeta-contenedorCantidad">
            <button onClick={() => setCantidad(Math.max(1, cantidad - 1))}>-</button>
            <input type="number" value={cantidad} readOnly />
            <button onClick={() => setCantidad(cantidad + 1)}>+</button>
          </div>
        </div>

        <p className="tarjeta-precio">${(precio * cantidad).toLocaleString('es-CO')}</p>

        <button 
          onClick={handleAgregar} 
          className="tarjeta-boton" 
          disabled={!colorSeleccionado || (tallas.length > 0 && !tallaSeleccionada)}
        >
          Agregar al Pedido
        </button>
      </div>
    </div>
  );
};

export default ElementoTarjeta;
