import React, { useState, useRef, useEffect } from 'react';
import '../css/agente.css'; // Usaremos el CSS que ya existe en el proyecto

const Agente = ({ onSelectCategory, categorias = [] }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  
  const initialMessage = { 
    from: 'bot', 
    text: '¬°Hola! Soy tu asistente virtual. ¬øC√≥mo puedo ayudarte hoy?', 
    options: ['Ver Productos', 'Medios de Pago', 'Horarios de Atenci√≥n'] 
  };

  useEffect(() => {
    setMessages([initialMessage]);
  }, []);

  const chatBodyRef = useRef(null);

  // Efecto para hacer scroll hacia abajo cada vez que se a√±ade un mensaje
  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
  }, [messages]);

  const toggleChat = () => {
    setIsOpen(!isOpen);
    if (!isOpen && messages.length === 0) {
      setMessages([initialMessage]);
    }
  };

  const handleOptionClick = (option) => {
    const userMessage = { from: 'user', text: option };
    let botResponse;

    if (categorias.includes(option)) {
      onSelectCategory(option);
      botResponse = { from: 'bot', text: `Perfecto, he filtrado el cat√°logo para mostrarte: ${option}. ¬øNecesitas algo m√°s?`, options: initialMessage.options };
    } else {
      switch (option) {
        case 'Ver Productos':
          botResponse = { from: 'bot', text: '¬°Claro! Elige una categor√≠a para ver nuestros productos.', options: [...categorias, 'Volver al men√∫ principal'] };
          break;
        case 'Medios de Pago':
          botResponse = { from: 'bot', text: 'Aceptamos tarjetas de cr√©dito, d√©bito, y transferencias por Nequi y Daviplata.', options: ['Volver al men√∫ principal'] };
          break;
        case 'Horarios de Atenci√≥n':
          botResponse = { from: 'bot', text: 'Nuestros horarios son de Lunes a S√°bado, de 9:00 AM a 6:00 PM.', options: ['Volver al men√∫ principal'] };
          break;
        case 'Volver al men√∫ principal':
          botResponse = initialMessage;
          break;
        default:
          botResponse = { from: 'bot', text: 'No entend√≠ esa opci√≥n, por favor elige una de las disponibles.', options: initialMessage.options };
          break;
      }
    }

    // Para "Volver al men√∫ principal", reemplazamos los mensajes para no alargar la conversaci√≥n
    if (option === 'Volver al men√∫ principal') {
      setMessages([userMessage, botResponse]);
    } else {
      setMessages([...messages, userMessage, botResponse]);
    }
  };

  const currentOptions = messages[messages.length - 1]?.options || [];

  return (
    <div className="chatbot-container">
      <div className={`chat-window ${isOpen ? 'open' : ''}`}>
        <div className="chat-header">
          <h2>Asistente Virtual</h2>
          <button onClick={toggleChat} className="close-btn">&times;</button>
        </div>
        <div className="chat-body" ref={chatBodyRef}>
          {messages.map((msg, index) => (
            <div key={index} className={`message-container ${msg.from}`}>
              <div className={`message ${msg.from}`}>
                {msg.text}
              </div>
            </div>
          ))}
        </div>
        <div className="chat-footer">
          {currentOptions.map((option, index) => (
            <button key={index} onClick={() => handleOptionClick(option)} className="option-btn">
              {option}
            </button>
          ))}
        </div>
      </div>
      <button onClick={toggleChat} className="chat-toggle-btn">
        <span>üí¨</span>
      </button>
    </div>
  );
};

export default Agente;
