import React from 'react';

const Productos = ({ accionActiva, formData, setFormData }) => {
  const campos = [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre', type: 'text' },
    { name: 'categoria', label: 'Categoría', type: 'text' },
    { name: 'precio', label: 'Precio', type: 'number' },
    { 
      name: 'estado', 
      label: 'Estado', 
      type: 'select', 
      options: ['Activo', 'Dado de baja'] 
    },
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div>
      {campos.map(({ name, label, type, options }) => {
        if(type === 'select') {
          return (
            <div key={name} style={{ marginBottom: 12 }}>
              <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                {label}:
              </label>
              <select
                id={name}
                name={name}
                value={formData[name] || 'Activo'}
                onChange={handleInputChange}
                required
                style={{
                  width: '100%',
                  padding: '0.4rem',
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              >
                <option value="">Seleccione...</option>
                {options.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>
          );
        }

        // Para inputs normales (text, number, etc)
        return (
          <div key={name} style={{ marginBottom: 12 }}>
            <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
              {label}:
            </label>
            <input
              id={name}
              name={name}
              type={type}
              value={formData[name] || ''}
              onChange={handleInputChange}
              required={accionActiva !== 'Buscar'}
              style={{
                width: '100%',
                padding: '0.4rem',
                borderRadius: 4,
                border: '1px solid #ccc',
              }}
            />
          </div>
        );
      })}
    </div>
  );
};

export default Productos;
