// PanelAdmin.js
import React, { useState } from 'react';

const modulos = [
  'InventarioAdministrador',
  'ProductosAdministrador',
  'ProveedoresAdministrador',
  'UsuariosAdministrador',
  'VentasAdministrador',
];

const acciones = [
  'Agregar',
  'Modificar',
  'Dar de baja',
  'Buscar',
  'Ver',
];

const PanelAdmin = () => {
  const [moduloActivo, setModuloActivo] = useState(modulos[0]);
  const [accionActiva, setAccionActiva] = useState(null);

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial, sans-serif' }}>
      <h1>Panel de Administración</h1>

      {/* Selección de módulo */}
      <div style={{ marginBottom: '1rem' }}>
        {modulos.map((modulo) => (
          <button
            key={modulo}
            onClick={() => { setModuloActivo(modulo); setAccionActiva(null); }}
            style={{
              marginRight: '0.5rem',
              padding: '0.5rem 1rem',
              backgroundColor: modulo === moduloActivo ? '#007bff' : '#eee',
              color: modulo === moduloActivo ? 'white' : 'black',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
          >
            {modulo.replace('Administrador', '')}
          </button>
        ))}
      </div>

      {/* Opciones de acción */}
      <div style={{ marginBottom: '1rem' }}>
        {acciones.map((accion) => (
          <button
            key={accion}
            onClick={() => setAccionActiva(accion)}
            style={{
              marginRight: '0.5rem',
              padding: '0.4rem 0.8rem',
              backgroundColor: accion === accionActiva ? '#28a745' : '#ddd',
              color: accion === accionActiva ? 'white' : 'black',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
            }}
          >
            {accion}
          </button>
        ))}
      </div>

      {/* Contenido dinámico */}
      <div style={{ border: '1px solid #ccc', padding: '1rem', borderRadius: '6px' }}>
        {!accionActiva && <p>Selecciona una acción para el módulo <b>{moduloActivo}</b>.</p>}

        {accionActiva && (
          <div>
            <h3>
              {accionActiva} - {moduloActivo.replace('Administrador', '')}
            </h3>
            <p>
              Aquí puedes implementar la funcionalidad para <b>{accionActiva}</b> en el módulo <b>{moduloActivo}</b>.
            </p>
            {/* Aquí iría el formulario o componente correspondiente */}
          </div>
        )}
      </div>
    </div>
  );
};

export default PanelAdmin;
