import React, { useState } from 'react';
import Tarjeta from '../elementos/Tarjeta';
import { productos } from './productos';

const Catalogo = () => {
  const [carrito, setCarrito] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState(''); // NUEVO

  const handleAgregarAlCarrito = (producto) => {
    setCarrito((prev) => [...prev, producto]);
  };

  // FILTRAR productos por categoría seleccionada
  const productosFiltrados = categoriaSeleccionada
    ? productos.filter((p) => p.categoria === categoriaSeleccionada)
    : productos;

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '1fr 6fr 1fr', padding: '15px', gap: '10px' }}>
      {/* Menú de Categorías */}
      <div>
        <h3>Categorías</h3>
        <ul style={{ listStyle: 'none', padding: 0, cursor: 'pointer' }}>
          {[
            'Boxer sin costuras',
            'Boxer sin costura',
            'Boxer microfibra deportivo',
            'Boxer algodón premium',
            'Boxer mezcla stretch',
            'Boxer compresión ligera',
          ].map((cat) => (
            <li key={cat} onClick={() => setCategoriaSeleccionada(cat)}>
              {cat}
            </li>
          ))}
          <li onClick={() => setCategoriaSeleccionada('')}>Ver todo</li>
        </ul>
      </div>

      {/* Productos */}
      <div>
        <h2>Catálogo</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
          {productosFiltrados.map((producto) => (
            <Tarjeta
              key={producto.id}
              imagen={producto.imagen}
              nombre={producto.nombre}
              descripcion={producto.descripcion}
              precio={producto.precio}
              colores={producto.colores}
              tallas={producto.tallas}
              onAgregar={() => handleAgregarAlCarrito(producto)}
            />
          ))}
        </div>
      </div>

      {/* Carrito */}
      <div>
        <h3>Carrito</h3>
        <ul>
          {carrito.map((item, index) => (
            <li key={index}>{item.nombre} - ${item.precio}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Catalogo;
