import React from 'react';
import Tarjeta from '../elementos/ElementoTarjeta';
import ElementoPedido from '../elementos/ElementoCarrito';
import ElementoTitulo from '../elementos/ElementoTitulo';
import '../css/catalogo.css';

const VistaACatalogo = ({
  categoriaSeleccionada,
  productos,
  pedido,
  agregarAlPedido,
  vaciarPedido,
  colores = [],
  tallas = []
}) => {
  // Función para obtener la URL correcta de la imagen
  const getImageUrl = (producto) => {
    if (producto.imagen) {
      // Si ya tiene la ruta completa del servidor, usarla
      if (producto.imagen.startsWith('/images/')) {
        return `http://localhost:5000${producto.imagen}`;
      }
      // Si es una ruta relativa, construir la URL completa
      return `http://localhost:5000/images/${producto.imagen}`;
    }
    // Imagen por defecto si no hay imagen
    return '/logo192.png';
  };

  // Filtrar productos por categoría
  const productosFiltrados = categoriaSeleccionada === 'Todos' 
    ? productos 
    : productos.filter(producto => producto.categoria === categoriaSeleccionada);

  return (
    <div className="catalogo-container">
      <div className="catalogo-productos">
        <ElementoTitulo texto="Gardem" />
        <h2>{categoriaSeleccionada}</h2>
        <div className="catalogo-grid">
          {productosFiltrados.length > 0 ? (
            productosFiltrados.map((producto) => (
              <Tarjeta
                key={producto.id}
                id={producto.id}
                imagen={getImageUrl(producto)}
                nombre={producto.nombre}
                descripcion={producto.descripcion}
                precio={producto.precio}
                colores={producto.colores ? producto.colores.split(',').map(c => c.trim()) : []}
                tallas={producto.tallas ? producto.tallas.split(',').map(t => t.trim()) : []}
                coloresDisponibles={colores}
                tallasDisponibles={tallas}
                onAgregar={(opciones) => agregarAlPedido({
                  id: producto.id,
                  nombre: producto.nombre,
                  precio: producto.precio,
                  imagen: producto.imagen,
                  color: opciones.color,
                  talla: opciones.talla,
                  cantidad: opciones.cantidad
                })}
              />
            ))
          ) : (
            <p>No hay productos que coincidan con tu búsqueda.</p>
          )}
        </div>
      </div>

      <ElementoPedido items={pedido} vaciarPedido={vaciarPedido} />
    </div>
  );
};

export default VistaACatalogo;
