import React, { useState } from 'react';
import '../css/agente.css';

const ElementoAgente = () => {
  const [step, setStep] = useState(1);
  const [datos, setDatos] = useState({
    nombre: '',
    direccion: '',
    telefono: '',
    pedido: '',
    pago: '',
  });

  const handleChange = (e) => {
    setDatos({ ...datos, [e.target.name]: e.target.value });
  };

  const siguiente = () => setStep(step + 1);
  const volver = () => setStep(step - 1);

  return (
    <div>
      {step === 1 && (
        <>
          <p>¡Hola! Soy tu ElementoAgente virtual. ¿Cómo te llamas?</p>
          <input
            name="nombre"
            value={datos.nombre}
            onChange={handleChange}
            placeholder="Tu nombre completo"
          />
          <button disabled={!datos.nombre} onClick={siguiente}>
            Siguiente
          </button>
        </>
      )}

      {step === 2 && (
        <>
          <p>Perfecto {datos.nombre}, ¿cuál es tu dirección de envío?</p>
          <input
            name="direccion"
            value={datos.direccion}
            onChange={handleChange}
            placeholder="Dirección completa"
          />
          <button onClick={volver}>Volver</button>
          <button disabled={!datos.direccion} onClick={siguiente}>
            Siguiente
          </button>
        </>
      )}

      {step === 3 && (
        <>
          <p>¿Cuál es tu número de teléfono para contacto?</p>
          <input
            name="telefono"
            value={datos.telefono}
            onChange={handleChange}
            placeholder="Teléfono"
          />
          <button onClick={volver}>Volver</button>
          <button disabled={!datos.telefono} onClick={siguiente}>
            Siguiente
          </button>
        </>
      )}

      {step === 4 && (
        <>
          <p>Cuéntame qué productos deseas pedir (cantidad, modelo, etc.)</p>
          <input
            name="pedido"
            value={datos.pedido}
            onChange={handleChange}
            placeholder="Detalle del pedido"
          />
          <button onClick={volver}>Volver</button>
          <button disabled={!datos.pedido} onClick={siguiente}>
            Siguiente
          </button>
        </>
      )}

      {step === 5 && (
        <>
          <p>¿Cómo deseas pagar? (efectivo, tarjeta, transferencia)</p>
          <input
            name="pago"
            value={datos.pago}
            onChange={handleChange}
            placeholder="Método de pago"
          />
          <button onClick={volver}>Volver</button>
          <button disabled={!datos.pago} onClick={siguiente}>
            Confirmar Pedido
          </button>
        </>
      )}

      {step === 6 && (
        <>
          <p>
            ¡Gracias por tu pedido, {datos.nombre}! Lo estamos procesando y te
            contactaremos pronto.
          </p>
          <button onClick={() => setStep(1)}>Hacer otro pedido</button>
        </>
      )}
    </div>
  );
};

export default ElementoAgente;
