// Importamos React y los hooks necesarios
import React, { useState } from 'react';
import Tarjeta from '../elementos/Tarjeta';         // Componente visual para cada producto
import { productos } from './productos';           // Lista de productos con categoría
import '../css/catalogo.css';                      // Hoja de estilo CSS

const Catalogo = () => {
  // Estado para manejar productos agregados al carrito
  const [carrito, setCarrito] = useState([]);

  // Estado para guardar la categoría seleccionada
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('');

  // Agrega un producto al carrito
  const handleAgregarAlCarrito = (producto) => {
    setCarrito((prev) => [...prev, producto]);
  };

  // Filtrar productos según la categoría seleccionada
  // Usamos .toLowerCase().trim() para asegurar coincidencias aunque haya diferencias de mayúsculas o espacios
  const productosFiltrados = categoriaSeleccionada
    ? productos.filter(
        (p) =>
          p.categoria.toLowerCase().trim() ===
          categoriaSeleccionada.toLowerCase().trim()
      )
    : productos;

  return (
    <div className="catalogo-container">
      {/* --- Menú lateral de categorías --- */}
      <div className="catalogo-categorias">
        <h3>Categorías</h3>
        <ul>
          {/* Lista de categorías. Cuando se hace clic, se actualiza el estado de categoríaSeleccionada */}
          {[
            'Boxer sin costuras',
            'Boxer sin costura',
            'Boxer microfibra deportivo',
            'Boxer algodón premium',
            'Boxer mezcla stretch',
            'Boxer compresión ligera',
          ].map((cat) => (
            <li key={cat} onClick={() => setCategoriaSeleccionada(cat)}>
              {cat}
            </li>
          ))}
          {/* Opción para ver todos los productos */}
          <li onClick={() => setCategoriaSeleccionada('')}>Ver todo</li>
        </ul>
      </div>

      {/* --- Sección de productos --- */}
      <div className="catalogo-productos">
        <h2>Catálogo</h2>

        {/* Mostrar la categoría seleccionada (útil para depurar) */}
        {categoriaSeleccionada && (
          <p className="categoria-activa">Mostrando: {categoriaSeleccionada}</p>
        )}

        <div className="catalogo-grid">
          {/* Mostrar tarjetas de producto ya filtradas */}
          {productosFiltrados.map((producto) => (
            <Tarjeta
              key={producto.id}
              imagen={producto.imagen}
              nombre={producto.nombre}
              descripcion={producto.descripcion}
              precio={producto.precio}
              colores={producto.colores}
              tallas={producto.tallas}
              onAgregar={() => handleAgregarAlCarrito(producto)}
            />
          ))}
        </div>
      </div>

      {/* --- Carrito lateral --- */}
      <div className="catalogo-carrito">
        <h3>Carrito</h3>
        <ul>
          {carrito.map((item, index) => (
            <li key={index}>
              {item.nombre} - ${item.precio}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Catalogo;
