import React, { useState } from 'react';
import Tarjeta from '../elementos/Tarjeta';
import { productos } from './productos';

const Catalogo = () => {
  const [carrito, setCarrito] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState(null);

  // Agrupar productos por categor√≠a
  const productosPorCategoria = productos.reduce((acc, producto) => {
    const { categoria } = producto;
    if (!acc[categoria]) acc[categoria] = [];
    acc[categoria].push(producto);
    return acc;
  }, {});

  const handleAgregarAlCarrito = (producto) => {
    setCarrito(prev => [...prev, producto]);
  };

  const categorias = Object.keys(productosPorCategoria);

  const productosAMostrar = categoriaSeleccionada
    ? productosPorCategoria[categoriaSeleccionada]
    : productos;

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      
      {/* üß≠ Columna izquierda: Men√∫ de categor√≠as */}
      <div style={{ width: '20%', padding: '20px', borderRight: '1px solid #ccc' }}>
        <h3>Categor√≠as</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {categorias.map(cat => (
            <li key={cat} style={{ marginBottom: '10px', cursor: 'pointer' }}
                onClick={() => setCategoriaSeleccionada(cat)}>
              {cat}
            </li>
          ))}
          <li
            style={{ marginTop: '20px', color: 'blue', cursor: 'pointer' }}
            onClick={() => setCategoriaSeleccionada(null)}
          >
            Mostrar todas
          </li>
        </ul>
      </div>

      {/* üõçÔ∏è Columna central: Productos */}
      <div style={{ width: '60%', padding: '20px', overflowY: 'auto' }}>
        <h2>{categoriaSeleccionada || 'Todos los productos'}</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '15px' }}>
          {productosAMostrar.map(producto => (
            <Tarjeta
              key={producto.id}
              imagen={producto.imagen}
              nombre={producto.nombre}
              descripcion={producto.descripcion}
              precio={producto.precio}
              colores={producto.colores}
              tallas={producto.tallas}
              onAgregar={() => handleAgregarAlCarrito(producto)}
            />
          ))}
        </div>
      </div>

      {/* üõí Columna derecha: Carrito */}
      <div style={{ width: '20%', padding: '20px', borderLeft: '1px solid #ccc', background: '#f9f9f9' }}>
        <h3>Carrito</h3>
        <ul>
          {carrito.map((item, index) => (
            <li key={index}>
              {item.nombre} - ${item.precio}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Catalogo;
