import React, { useState, useRef, useEffect } from 'react';
import '../css/agente.css'; 
import avatar from '../img/logo.jpg'; // Importamos el avatar

const Agente = ({ onSelectCategory, categorias = [] }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  
  const initialMessage = { 
    from: 'bot', 
    text: 'Soy Titan Jim, tu estilista personal. Estoy aquí para asegurar que encuentres prendas de calidad superior. ¿Qué necesitas hoy?', 
    options: ['Explorar Catálogo', 'Opciones de Pago', 'Disponibilidad'] 
  };

  // Cargar el mensaje inicial
  useEffect(() => {
    setMessages([initialMessage]);
  }, []);

  const chatBodyRef = useRef(null);

  // Scroll automático
  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
  }, [messages]);

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (option) => {
    const userMessage = { from: 'user', text: option };
    let botResponse;

    if (categorias.includes(option)) {
      onSelectCategory(option);
      botResponse = { from: 'bot', text: `Buena elección. He seleccionado todos los productos de ${option}. ¿Necesitas algo más?`, options: initialMessage.options };
    } else {
      switch (option) {
        case 'Explorar Catálogo':
          botResponse = { from: 'bot', text: 'Excelente. Estas son nuestras líneas de productos. Selecciona una para verla en detalle.', options: [...categorias, 'Volver'] };
          break;
        case 'Opciones de Pago':
          botResponse = { from: 'bot', text: 'Aceptamos todas las tarjetas de crédito y débito, además de Nequi y Daviplata para tu conveniencia.', options: ['Volver'] };
          break;
        case 'Disponibilidad':
          botResponse = { from: 'bot', text: 'Nuestro horario de atención es de Lunes a Sábado, de 9:00 AM a 6:00 PM.', options: ['Volver'] };
          break;
        case 'Volver':
          botResponse = initialMessage;
          break;
        default:
          botResponse = { from: 'bot', text: 'No reconozco esa opción. Por favor, elige una de las disponibles.', options: initialMessage.options };
          break;
      }
    }

    if (option === 'Volver') {
      setMessages([userMessage, botResponse]);
    } else {
      setMessages([...messages, userMessage, botResponse]);
    }
  };

  const currentOptions = messages[messages.length - 1]?.options || [];

  return (
    <div className="chatbot-container">
      <div className={`chat-window ${isOpen ? 'open' : ''}`}>
        <div className="chat-header">
          <img src={avatar} alt="Avatar de Titan Jim" className="chat-avatar" />
          <h2>Titan Jim</h2>
          <button onClick={toggleChat} className="close-btn">&times;</button>
        </div>
        <div className="chat-body" ref={chatBodyRef}>
          {messages.map((msg, index) => (
            <div key={index} className={`message-container ${msg.from}`}>
              <div className={`message ${msg.from}`}>
                {msg.text}
              </div>
            </div>
          ))}
        </div>
        <div className="chat-footer">
          {currentOptions.map((option, index) => (
            <button key={index} onClick={() => handleOptionClick(option)} className="option-btn">
              {option}
            </button>
          ))}
        </div>
      </div>
      <button onClick={toggleChat} className="chat-toggle-btn">
        <span>💬</span>
      </button>
    </div>
  );
};

export default Agente;
