import React, { useState } from 'react';

import Inventario from './VistaAInventario';
import Productos from '../elementos/ElementoNavProductos';
import Proveedores from './vistaProveedores';
import Ventas from './VistaVentas';

const VistaAPanelAdmin = () => {
  const [moduloActivo, setModuloActivo] = useState('Inventario');
  const [accionActiva, setAccionActiva] = useState(null);
  const [formData, setFormData] = useState({});

  const modulos = ['Inventario', 'Productos', 'Proveedores', 'Ventas']; // 'Usuarios' eliminado
  const acciones = ['Agregar', 'Modificar', 'Buscar', 'Dar de Baja', 'Habilitar', 'Salidas', 'Entradas', 'Ver'];

  const handleModuloChange = (modulo) => {
    setModuloActivo(modulo);
    setAccionActiva(null);
    setFormData({});
  };

  const handleAccionChange = (accion) => {
    setAccionActiva(accion);
    setFormData({});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Acción: ${accionActiva}\nMódulo: ${moduloActivo}\nDatos: ${JSON.stringify(formData, null, 2)}`);
  };

  return (
    <div style={{ padding: '1rem', maxWidth: 600, margin: 'auto' }}>
      <h2>Panel Administrativo</h2>

      {/* --- Selección de módulo --- */}
      <div style={{ marginBottom: '1rem' }}>
        <strong>Selecciona módulo:</strong>
        <div style={{ display: 'flex', gap: '0.5rem', marginTop: '0.5rem', flexWrap: 'wrap' }}>
          {modulos.map(modulo => (
            <button
              key={modulo}
              onClick={() => handleModuloChange(modulo)}
              style={{
                padding: '0.5rem 1rem',
                backgroundColor: modulo === moduloActivo ? '#007bff' : '#eee',
                color: modulo === moduloActivo ? 'white' : 'black',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              {modulo}
            </button>
          ))}
        </div>
      </div>

      {/* --- Selección de acción --- */}
      <div style={{ marginBottom: '1rem' }}>
        <strong>Selecciona acción:</strong>
        <div style={{ display: 'flex', gap: '0.5rem', marginTop: '0.5rem', flexWrap: 'wrap' }}>
          {acciones.map(accion => (
            <button
              key={accion}
              onClick={() => handleAccionChange(accion)}
              style={{
                padding: '0.5rem 1rem',
                backgroundColor: accion === accionActiva ? '#28a745' : '#eee',
                color: accion === accionActiva ? 'white' : 'black',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              {accion}
            </button>
          ))}
        </div>
      </div>

      {/* --- Formulario dinámico según módulo y acción --- */}
      {accionActiva && accionActiva !== 'Ver' && (
        <form onSubmit={handleSubmit} style={{ border: '1px solid #ccc', padding: '1rem', borderRadius: 6 }}>
          <h3>{accionActiva} - {moduloActivo}</h3>

          {/* ========== Inventario ========== */}
          {moduloActivo === 'Inventario' && (
            <>
              {/* Inventario - Todas las acciones */}
              <Inventario accionActiva={accionActiva} formData={formData} setFormData={setFormData} />
            </>
          )}

          {/* ========== Productos ========== */}
          {moduloActivo === 'Productos' && (
            <>
              {/* Productos - Todas las acciones */}
              <Productos accionActiva={accionActiva} formData={formData} setFormData={setFormData} />
            </>
          )}

          {/* ========== Proveedores ========== */}
          {moduloActivo === 'Proveedores' && (
            <>
              {/* Proveedores - Todas las acciones */}
              <Proveedores accionActiva={accionActiva} formData={formData} setFormData={setFormData} />
            </>
          )}

          {/* ========== Ventas ========== */}
          {moduloActivo === 'Ventas' && (
            <>
              {/* Ventas - Todas las acciones */}
              <Ventas accionActiva={accionActiva} formData={formData} setFormData={setFormData} />
            </>
          )}

          {/* --- Botón enviar --- */}
          <button
            type="submit"
            style={{
              marginTop: '1rem',
              backgroundColor: '#007bff',
              color: 'white',
              padding: '0.6rem 1.2rem',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer',
            }}
          >
            {accionActiva}
          </button>
        </form>
      )}
    </div>
  );
};

export default VistaAPanelAdmin;
