// Importamos React y los hooks useState y useEffect para manejar estado y efectos secundarios
import React, { useState, useEffect } from 'react';

// Importamos el componente CreatableSelect para dropdowns que permiten crear nuevas opciones
import CreatableSelect from 'react-select/creatable';

// Definimos los módulos disponibles en el panel
const modulos = [
  'Inventario',
  'Productos',
  'Proveedores',
  'Usuarios',
  'Ventas',
];

// Definimos las acciones que se pueden realizar en cada módulo
const acciones = [
  'Agregar',
  'Modificar',
  'Dar de baja',
  'Buscar',
  'Ver',
];

// Definimos los campos que corresponden a cada módulo
const camposPorModulo = {
  Inventario: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre del Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'numero_factura_orden', label: 'Número Factura u Orden', type: 'text' },
    { name: 'ubicacion', label: 'Ubicación', type: 'text' },
    { 
      name: 'custodia', 
      label: 'Custodia', 
      type: 'select', 
      options: ['Área A', 'Área B', 'Área C'] 
    },
    { 
      name: 'proveedor', 
      label: 'Proveedor', 
      type: 'select', 
      options: ['Proveedor 1', 'Proveedor 2', 'Proveedor 3'] 
    },
    { name: 'fecha_ingreso', label: 'Fecha de Ingreso', type: 'datetime-local' },
    { name: 'fecha_entrega', label: 'Fecha de Entrega', type: 'datetime-local' },
    { name: 'tipo_movimiento', label: 'Tipo de Movimiento', type: 'creatable-select' },
    { name: 'orden_salida', label: 'Orden de Salida', type: 'text' },
    { name: 'descripcion', label: 'Descripción', type: 'textarea' },
  ],

  Productos: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre', type: 'text' },
    { name: 'categoria', label: 'Categoría', type: 'text' },
    { name: 'precio', label: 'Precio', type: 'number' },
  ],

  Proveedores: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre', type: 'text' },
    { name: 'telefono', label: 'Teléfono', type: 'text' },
    { name: 'email', label: 'Email', type: 'email' },
  ],

  Usuarios: [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'usuario', label: 'Usuario', type: 'text' },
    { name: 'email', label: 'Email', type: 'email' },
    { name: 'rol', label: 'Rol', type: 'text' },
  ],

  Ventas: [
    { name: 'id', label: 'ID Venta', type: 'number' },
    { name: 'producto', label: 'Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'total', label: 'Total', type: 'number' },
  ],
};

// Componente principal del panel de administración
const PanelAdmin = () => {
  // Estado para el módulo activo (por defecto el primero)
  const [moduloActivo, setModuloActivo] = useState(modulos[0]);

  // Estado para la acción activa (por defecto null)
  const [accionActiva, setAccionActiva] = useState(null);

  // Estado para los datos del formulario
  const [formData, setFormData] = useState({});

  // Estado para el valor seleccionado en creatable select
  const [creatableValue, setCreatableValue] = useState(null);

  // Cuando cambia módulo o acción, reseteamos el formulario y el valor creatable
  useEffect(() => {
    setFormData({});
    setCreatableValue(null);
  }, [moduloActivo, accionActiva]);

  // Obtenemos los campos correspondientes al módulo activo
  const campos = camposPorModulo[moduloActivo] || [];

  // Función para manejar cambios en inputs normales (text, number, etc)
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  // Función para manejar cambios en el CreatableSelect
  const handleCreatableChange = (selectedOption, name) => {
    setCreatableValue(selectedOption);
    setFormData(prev => ({
      ...prev,
      [name]: selectedOption ? selectedOption.value : '',
    }));
  };

  // Función para manejar el submit del formulario
  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Acción: ${accionActiva}\nMódulo: ${moduloActivo}\nDatos: ${JSON.stringify(formData, null, 2)}`);
  };

  return (
    <div style={{ padding: '1rem', fontFamily: 'Arial, sans-serif', maxWidth: 600 }}>
      <h1>Panel de Administración</h1>

      {/* Sección de botones para seleccionar módulo */}
      <div style={{ marginBottom: '1rem' }}>
        {modulos.map(m => (
          <button
            key={m}
            onClick={() => { setModuloActivo(m); setAccionActiva(null); }}
            style={{
              marginRight: 8,
              padding: '0.5rem 1rem',
              backgroundColor: m === moduloActivo ? '#007bff' : '#eee',
              color: m === moduloActivo ? 'white' : 'black',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer',
            }}
          >
            {m}
          </button>
        ))}
      </div>

      {/* Sección de botones para seleccionar acción */}
      <div style={{ marginBottom: '1rem' }}>
        {acciones.map(a => (
          <button
            key={a}
            onClick={() => setAccionActiva(a)}
            style={{
              marginRight: 8,
              padding: '0.4rem 0.8rem',
              backgroundColor: a === accionActiva ? '#28a745' : '#ddd',
              color: a === accionActiva ? 'white' : 'black',
              border: 'none',
              borderRadius: 4,
              cursor: 'pointer',
            }}
          >
            {a}
          </button>
        ))}
      </div>

      {/* Contenido del panel según la acción */}
      <div style={{ border: '1px solid #ccc', padding: 16, borderRadius: 6 }}>
        {/* Si no hay acción seleccionada */}
        {!accionActiva && (
          <p>Selecciona una acción para el módulo <b>{moduloActivo}</b>.</p>
        )}

        {/* Si la acción es "Ver" mostramos un placeholder */}
        {(accionActiva === 'Ver') && (
          <div>
            <h3>Datos del módulo <b>{moduloActivo}</b></h3>
            <p>Aquí se mostrarían los datos para visualizar.</p>
          </div>
        )}

        {/* Para las demás acciones mostramos el formulario */}
        {(accionActiva && accionActiva !== 'Ver Mercancía') && (
          <form onSubmit={handleSubmit}>
            <h3>{accionActiva} - {moduloActivo}</h3>

            {/* Renderizamos los campos dinámicamente según su tipo */}
            {campos.map(({ name, label, type, options }) => {
              if (type === 'select') {
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                      {label}:
                    </label>
                    <select
                      id={name}
                      name={name}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                      disabled={accionActiva === 'Dar de baja'}
                      style={{
                        width: '100%',
                        padding: '0.4rem',
                        borderRadius: 4,
                        border: '1px solid #ccc',
                      }}
                    >
                      <option value="">Seleccione...</option>
                      {options && options.map(opt => (
                        <option key={opt} value={opt}>{opt}</option>
                      ))}
                    </select>
                  </div>
                );
              } else if (type === 'creatable-select') {
                // Opciones por defecto para tipo_movimiento
                const opcionesMovimiento = [
                  { value: 'Ingreso de Mercancía', label: 'Ingreso de Mercancía' },
                  { value: 'Traslado', label: 'Traslado de Mercancía' },
                  { value: 'Devolución', label: 'Devolución de Mercancía' },
                  { value: 'Garantía', label: 'Garantía de Mercancía' },
                ];
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label style={{ display: 'block', marginBottom: 4 }}>{label}:</label>
                    <CreatableSelect
                      isClearable
                      options={opcionesMovimiento}
                      onChange={(selected) => handleCreatableChange(selected, name)}
                      value={creatableValue}
                      placeholder="Seleccione o escriba una opción"
                    />
                  </div>
                );
              } else if (type === 'textarea') {
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                      {label}:
                    </label>
                    <textarea
                      id={name}
                      name={name}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      rows={4}
                      style={{
                        width: '100%',
                        padding: '0.5rem',
                        borderRadius: 4,
                        border: '1px solid #ccc',
                      }}
                      required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                      disabled={accionActiva === 'Dar de baja'}
                    />
                  </div>
                );
              } else {
                // Input estándar para text, number, datetime-local, email, etc.
                return (
                  <div key={name} style={{ marginBottom: 12 }}>
                    <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                      {label}:
                    </label>
                    <input
                      id={name}
                      name={name}
                      type={type}
                      value={formData[name] || ''}
                      onChange={handleInputChange}
                      required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                      disabled={accionActiva === 'Dar de baja'}
                      style={{
                        width: '100%',
                        padding: '0.4rem',
                        borderRadius: 4,
                        border: '1px solid #ccc',
                      }}
                    />
                  </div>
                );
              }
            })}

            {/* Botón para enviar el formulario */}
            <button
              type="submit"
              style={{
                backgroundColor: '#007bff',
                color: 'white',
                padding: '0.6rem 1.2rem',
                border: 'none',
                borderRadius: 4,
                cursor: 'pointer',
              }}
            >
              Enviar
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default PanelAdmin;
