import React from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => (
  <div style={{ border: '1px solid #ccc', borderRadius: 8, padding: 16, margin: 8, width: 250 }}>
    <img src={imagen} alt={nombre} style={{ width: '100%', borderRadius: 4 }} />
    <h3>{nombre}</h3>
    <p>{descripcion}</p>
    <p><strong>Precio:</strong> ${precio}</p>
    {colores && <p><strong>Colores:</strong> {colores.join(', ')}</p>}
    {tallas && <p><strong>Tallas:</strong> {tallas.join(', ')}</p>}

    <button onClick={onAgregar} style={{ marginTop: '10px', padding: '8px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}>
      Agregar al carrito
    </button>
  </div>
);

export default Tarjeta;
