import React, { useState, useEffect } from 'react';
import '../css/carrucel.css'; // Ajusta el path si es necesario

const mensajes = [
  "Compra ahora y pague después con Sistecrédito y Addi",
  "3 Panties x $189.000 en estilos seleccionados",
  "Envíos gratis por compras superiores a $289.900",
  "Envíos al día siguiente en Bogotá, solo aplica de lunes a viernes.",
];

const Carrucel = () => {
  const [indice, setIndice] = useState(0);

  useEffect(() => {
    const intervalo = setInterval(() => {
      setIndice((prevIndice) => (prevIndice + 1) % mensajes.length);
    }, 4000); // cambia cada 4 segundos

    return () => clearInterval(intervalo);
  }, []);

  
};

export default Carrucel;
