import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div style={{ 
      border: '1px solid #ccc', 
      borderRadius: 8, 
      padding: 16, 
      margin: 8, 
      width: 250,
      boxSizing: 'border-box',
      position: 'relative'
    }}>
      <img 
        src={imagen} 
        alt={nombre} 
        style={{ width: '100%', borderRadius: 4, cursor: 'pointer' }}
        onClick={() => setShowInfo(!showInfo)} 
      />
      <h3>{nombre}</h3>
      <p>{descripcion}</p>
      <p><strong>Precio:</strong> ${precio}</p>
      {colores && <p><strong>Colores:</strong> {colores.join(', ')}</p>}
      {tallas && <p><strong>Tallas:</strong> {tallas.join(', ')}</p>}

      {/* Aquí va la info extra desplegable justo arriba del botón */}
      {showInfo && (
        <div 
          style={{ 
            borderTop: '1px solid #ddd', 
            padding: '10px 0', 
            marginTop: 10,
            backgroundColor: '#f9f9f9',
            borderRadius: '4px'
          }}
        >
          <p><em>Información extra o detalles adicionales aquí.</em></p>
          {/* Puedes poner más contenido aquí si quieres */}
        </div>
      )}

      <button 
        onClick={onAgregar} 
        style={{ 
          marginTop: 10, 
          padding: '8px', 
          backgroundColor: '#007bff', 
          color: 'white', 
          border: 'none', 
          borderRadius: '4px', 
          width: '100%', 
          cursor: 'pointer' 
        }}
      >
        Agregar al carrito
      </button>
    </div>
  );
};

export default Tarjeta;
