import React, { useState, useEffect } from 'react';
import { Plus, Search, Edit, Trash2, Eye, Package, Users, ShoppingCart, TrendingUp, TrendingDown, AlertCircle, CheckCircle, X, Save, ArrowLeft } from 'lucide-react';
import './../css/vistainventario.css';

const SistemaInventario = () => {
  // Estados principales
  const [vista, setVista] = useState('dashboard');
  const [modalAbierto, setModalAbierto] = useState(false);
  const [tipoModal, setTipoModal] = useState('');
  const [busqueda, setBusqueda] = useState('');
  const [itemSeleccionado, setItemSeleccionado] = useState(null);
  const [mensaje, setMensaje] = useState({ tipo: '', texto: '' });
  const [productos, setProductos] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [loading, setLoading] = useState(true);

  // Estados para formularios
  const [formProducto, setFormProducto] = useState({
    nombre: '',
    descripcion: '',
    precio: '',
    categoria_id: '',
    imagen: '',
    stock: ''
  });

  // Cargar datos al iniciar
  useEffect(() => {
    cargarDatos();
  }, []);

  const cargarDatos = async () => {
    try {
      setLoading(true);
      const [productosData, categoriasData] = await Promise.all([
        fetch('http://localhost:5000/api/productos').then(res => res.json()),
        fetch('http://localhost:5000/api/categorias').then(res => res.json())
      ]);
      setProductos(productosData);
      setCategorias(categoriasData);
    } catch (error) {
      console.error('Error cargando datos:', error);
      mostrarMensaje('error', 'Error al cargar los datos');
    } finally {
      setLoading(false);
    }
  };

  // Función para mostrar mensajes
  const mostrarMensaje = (tipo, texto) => {
    setMensaje({ tipo, texto });
    setTimeout(() => setMensaje({ tipo: '', texto: '' }), 3000);
  };

  // Función para abrir modal
  const abrirModal = (tipo, item = null) => {
    setTipoModal(tipo);
    setItemSeleccionado(item);
    if (item && tipo === 'editar') {
      setFormProducto({
        nombre: item.nombre,
        descripcion: item.descripcion,
        precio: item.precio,
        categoria_id: item.categoria_id || '',
        imagen: item.imagen,
        stock: item.stock
      });
    } else {
      setFormProducto({
        nombre: '',
        descripcion: '',
        precio: '',
        categoria_id: '',
        imagen: '',
        stock: ''
      });
    }
    setModalAbierto(true);
  };

  // Función para cerrar modal
  const cerrarModal = () => {
    setModalAbierto(false);
    setTipoModal('');
    setItemSeleccionado(null);
  };

  // Función para guardar producto
  const guardarProducto = async () => {
    try {
      const url = tipoModal === 'editar' 
        ? `http://localhost:5000/api/productos/${itemSeleccionado.id}`
        : 'http://localhost:5000/api/productos';
      
      const method = tipoModal === 'editar' ? 'PUT' : 'POST';
      
      const response = await fetch(url, {
        method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formProducto)
      });

      if (response.ok) {
        mostrarMensaje('exito', `Producto ${tipoModal === 'editar' ? 'actualizado' : 'creado'} exitosamente`);
        cerrarModal();
        cargarDatos();
      } else {
        throw new Error('Error en la operación');
      }
    } catch (error) {
      mostrarMensaje('error', 'Error al guardar el producto');
    }
  };

  // Función para eliminar producto
  const eliminarProducto = async (id) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este producto?')) {
      try {
        const response = await fetch(`http://localhost:5000/api/productos/${id}`, {
          method: 'DELETE'
        });
        
        if (response.ok) {
          mostrarMensaje('exito', 'Producto eliminado exitosamente');
          cargarDatos();
        } else {
          throw new Error('Error al eliminar');
        }
      } catch (error) {
        mostrarMensaje('error', 'Error al eliminar el producto');
      }
    }
  };

  // Filtrar productos por búsqueda
  const productosFiltrados = productos.filter(producto =>
    producto.nombre.toLowerCase().includes(busqueda.toLowerCase()) ||
    producto.descripcion.toLowerCase().includes(busqueda.toLowerCase())
  );

  // Estadísticas del dashboard
  const estadisticas = {
    totalProductos: productos.length,
    productosBajoStock: productos.filter(p => p.stock < 10).length,
    categorias: categorias.length,
    valorTotal: productos.reduce((sum, p) => sum + (p.precio * p.stock), 0)
  };

  if (loading) {
    return (
      <div className="admin-container">
        <div className="loading">Cargando panel de administración...</div>
      </div>
    );
  }

  return (
    <div className="admin-container">
      {/* Header */}
      <header className="admin-header">
        <div className="admin-header-content">
          <h1>Panel de Administración - Gardem</h1>
          <button 
            className="btn-volver"
            onClick={() => window.location.href = 'http://localhost:3001'}
          >
            <ArrowLeft size={20} />
            Volver al Catálogo
          </button>
        </div>
      </header>

      {/* Navegación */}
      <nav className="admin-nav">
        <button 
          className={`nav-btn ${vista === 'dashboard' ? 'active' : ''}`}
          onClick={() => setVista('dashboard')}
        >
          <TrendingUp size={20} />
          Dashboard
        </button>
        <button 
          className={`nav-btn ${vista === 'productos' ? 'active' : ''}`}
          onClick={() => setVista('productos')}
        >
          <Package size={20} />
          Productos
        </button>
        <button 
          className={`nav-btn ${vista === 'categorias' ? 'active' : ''}`}
          onClick={() => setVista('categorias')}
        >
          <ShoppingCart size={20} />
          Categorías
        </button>
      </nav>

      {/* Mensajes */}
      {mensaje.texto && (
        <div className={`mensaje ${mensaje.tipo}`}>
          {mensaje.tipo === 'exito' ? <CheckCircle size={20} /> : <AlertCircle size={20} />}
          {mensaje.texto}
        </div>
      )}

      {/* Contenido principal */}
      <main className="admin-main">
        {vista === 'dashboard' && (
          <div className="dashboard">
            <h2>Dashboard</h2>
            <div className="stats-grid">
              <div className="stat-card">
                <Package size={30} />
                <div>
                  <h3>{estadisticas.totalProductos}</h3>
                  <p>Total Productos</p>
                </div>
              </div>
              <div className="stat-card warning">
                <AlertCircle size={30} />
                <div>
                  <h3>{estadisticas.productosBajoStock}</h3>
                  <p>Bajo Stock</p>
                </div>
              </div>
              <div className="stat-card">
                <ShoppingCart size={30} />
                <div>
                  <h3>{estadisticas.categorias}</h3>
                  <p>Categorías</p>
                </div>
              </div>
              <div className="stat-card">
                <TrendingUp size={30} />
                <div>
                  <h3>${estadisticas.valorTotal.toLocaleString()}</h3>
                  <p>Valor Total</p>
                </div>
              </div>
            </div>
          </div>
        )}

        {vista === 'productos' && (
          <div className="productos-admin">
            <div className="productos-header">
              <h2>Gestión de Productos</h2>
              <div className="productos-actions">
                <div className="search-container">
                  <Search size={20} />
                  <input
                    type="text"
                    placeholder="Buscar productos..."
                    value={busqueda}
                    onChange={(e) => setBusqueda(e.target.value)}
                  />
                </div>
                <button 
                  className="btn-agregar"
                  onClick={() => abrirModal('crear')}
                >
                  <Plus size={20} />
                  Agregar Producto
                </button>
              </div>
            </div>

            <div className="productos-table">
              <table>
                <thead>
                  <tr>
                    <th>Imagen</th>
                    <th>Nombre</th>
                    <th>Categoría</th>
                    <th>Precio</th>
                    <th>Stock</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {productosFiltrados.map(producto => (
                    <tr key={producto.id}>
                      <td>
                        <img 
                          src={`http://localhost:5000${producto.imagen}`} 
                          alt={producto.nombre}
                          className="producto-thumb"
                        />
                      </td>
                      <td>{producto.nombre}</td>
                      <td>{producto.categoria_nombre}</td>
                      <td>${producto.precio.toLocaleString()}</td>
                      <td>
                        <span className={`stock ${producto.stock < 10 ? 'bajo' : 'normal'}`}>
                          {producto.stock}
                        </span>
                      </td>
                      <td>
                        <div className="acciones">
                          <button 
                            className="btn-accion ver"
                            onClick={() => abrirModal('ver', producto)}
                          >
                            <Eye size={16} />
                          </button>
                          <button 
                            className="btn-accion editar"
                            onClick={() => abrirModal('editar', producto)}
                          >
                            <Edit size={16} />
                          </button>
                          <button 
                            className="btn-accion eliminar"
                            onClick={() => eliminarProducto(producto.id)}
                          >
                            <Trash2 size={16} />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {vista === 'categorias' && (
          <div className="categorias-admin">
            <h2>Gestión de Categorías</h2>
            <div className="categorias-grid">
              {categorias.map(categoria => (
                <div key={categoria.id} className="categoria-card">
                  <h3>{categoria.nombre}</h3>
                  <p>{productos.filter(p => p.categoria_nombre === categoria.nombre).length} productos</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </main>

      {/* Modal */}
      {modalAbierto && (
        <div className="modal-overlay" onClick={cerrarModal}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h3>
                {tipoModal === 'crear' ? 'Crear Producto' : 
                 tipoModal === 'editar' ? 'Editar Producto' : 'Ver Producto'}
              </h3>
              <button className="btn-cerrar" onClick={cerrarModal}>
                <X size={20} />
              </button>
            </div>
            
            <div className="modal-body">
              {tipoModal === 'ver' ? (
                <div className="producto-detalle">
                  <img src={`http://localhost:5000${itemSeleccionado.imagen}`} alt={itemSeleccionado.nombre} />
                  <h4>{itemSeleccionado.nombre}</h4>
                  <p>{itemSeleccionado.descripcion}</p>
                  <p><strong>Precio:</strong> ${itemSeleccionado.precio.toLocaleString()}</p>
                  <p><strong>Stock:</strong> {itemSeleccionado.stock}</p>
                  <p><strong>Categoría:</strong> {itemSeleccionado.categoria_nombre}</p>
                </div>
              ) : (
                <form className="form-producto">
                  <div className="form-group">
                    <label>Nombre:</label>
                    <input
                      type="text"
                      value={formProducto.nombre}
                      onChange={(e) => setFormProducto({...formProducto, nombre: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Descripción:</label>
                    <textarea
                      value={formProducto.descripcion}
                      onChange={(e) => setFormProducto({...formProducto, descripcion: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-row">
                    <div className="form-group">
                      <label>Precio:</label>
                      <input
                        type="number"
                        value={formProducto.precio}
                        onChange={(e) => setFormProducto({...formProducto, precio: e.target.value})}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label>Stock:</label>
                      <input
                        type="number"
                        value={formProducto.stock}
                        onChange={(e) => setFormProducto({...formProducto, stock: e.target.value})}
                        required
                      />
                    </div>
                  </div>
                  <div className="form-group">
                    <label>Categoría:</label>
                    <select
                      value={formProducto.categoria_id}
                      onChange={(e) => setFormProducto({...formProducto, categoria_id: e.target.value})}
                      required
                    >
                      <option value="">Seleccionar categoría</option>
                      {categorias.map(cat => (
                        <option key={cat.id} value={cat.id}>{cat.nombre}</option>
                      ))}
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Imagen (ruta):</label>
                    <input
                      type="text"
                      value={formProducto.imagen}
                      onChange={(e) => setFormProducto({...formProducto, imagen: e.target.value})}
                      placeholder="/images/producto/imagen.jpg"
                      required
                    />
                  </div>
                </form>
              )}
            </div>
            
            {tipoModal !== 'ver' && (
              <div className="modal-footer">
                <button className="btn-cancelar" onClick={cerrarModal}>
                  Cancelar
                </button>
                <button className="btn-guardar" onClick={guardarProducto}>
                  <Save size={16} />
                  Guardar
                </button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

