import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div style={{ 
      display: 'flex', 
      border: '1px solid #ccc', 
      borderRadius: 8, 
      padding: 16, 
      margin: 16, 
      width: '30%',  // 3 tarjetas por fila (aprox)
      boxSizing: 'border-box',
      backgroundColor: '#fff',
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      minWidth: 280,  // para que no se haga demasiado pequeña en pantallas grandes
    }}>
      {/* Columna imagen */}
      <div style={{ flex: '1 1 40%', marginRight: 16 }}>
        <img 
          src={imagen} 
          alt={nombre} 
          style={{ width: '100%', borderRadius: 4, cursor: 'pointer' }}
          onClick={() => setShowInfo(!showInfo)} 
        />
      </div>

      {/* Columna info + carrito */}
      <div style={{ flex: '1 1 60%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
        <div>
          <h3>{nombre}</h3>
          <p>{descripcion}</p>
          <p><strong>Precio:</strong> ${precio}</p>
          {colores && <p><strong>Colores:</strong> {colores.join(', ')}</p>}
          {tallas && <p><strong>Tallas:</strong> {tallas.join(', ')}</p>}

          {/* Información adicional desplegable */}
          {showInfo && (
            <div style={{ 
              marginTop: 10, 
              padding: 10, 
              backgroundColor: '#f9f9f9', 
              borderRadius: 4, 
              boxShadow: 'inset 0 0 5px rgba(0,0,0,0.1)'
            }}>
              <p><em>Información adicional al hacer clic en la imagen.</em></p>
            </div>
          )}
        </div>

        {/* Botón carrito abajo */}
        <button 
          onClick={onAgregar} 
          style={{ 
            marginTop: 20, 
            padding: '12px', 
            backgroundColor: '#007bff', 
            color: 'white', 
            border: 'none', 
            borderRadius: 4, 
            cursor: 'pointer' 
          }}
        >
          Agregar al carrito
        </button>
      </div>
    </div>
  );
};

const ListaTarjetas = ({ productos, onAgregar }) => (
  <div style={{
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    backgroundColor: '#eee',
    padding: 32,
    minHeight: '100vh',
  }}>
    {productos.map(producto => (
      <Tarjeta 
        key={producto.id} 
        {...producto} 
        onAgregar={() => onAgregar(producto)} 
      />
    ))}
  </div>
);

export default ListaTarjetas;
