import React from 'react';
import '../css/p';

const Productos = ({ accionActiva, formData, setFormData }) => {
  const campos = [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre del Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'numero_factura_orden', label: 'Número Factura u Orden', type: 'text' },
    { name: 'ubicacion', label: 'Ubicación', type: 'text' },
    { 
      name: 'custodia', 
      label: 'Custodia', 
      type: 'select', 
      options: ['Área A', 'Área B', 'Área C'] 
    },
    { 
      name: 'proveedor', 
      label: 'Proveedor', 
      type: 'select', 
      options: ['Proveedor 1', 'Proveedor 2', 'Proveedor 3'] 
    },
    { name: 'fecha_ingreso', label: 'Fecha de Ingreso', type: 'datetime-local' },
    { name: 'fecha_entrega', label: 'Fecha de Entrega', type: 'datetime-local' },
    { name: 'descripcion', label: 'Descripción', type: 'textarea' },
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div>
      {campos.map(({ name, label, type, options }) => {
        if (type === 'select') {
          return (
            <div key={name} className="campo-container">
              <label htmlFor={name} className="campo-label">{label}:</label>
              <select
                id={name}
                name={name}
                value={formData[name] || ''}
                onChange={handleInputChange}
                disabled={accionActiva === 'Dar de baja'}
                required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                className="select-input"
              >
                <option value="">Seleccione...</option>
                {options.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>
          );
        } else if (type === 'textarea') {
          return (
            <div key={name} className="campo-container">
              <label htmlFor={name} className="campo-label">{label}:</label>
              <textarea
                id={name}
                name={name}
                value={formData[name] || ''}
                onChange={handleInputChange}
                rows={4}
                className="textarea-input"
                disabled={accionActiva === 'Dar de baja'}
                required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
              />
            </div>
          );
        } else {
          return (
            <div key={name} className="campo-container">
              <label htmlFor={name} className="campo-label">{label}:</label>
              <input
                id={name}
                name={name}
                type={type}
                value={formData[name] || ''}
                onChange={handleInputChange}
                disabled={accionActiva === 'Dar de baja'}
                required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                className="input-text"
              />
            </div>
          );
        }
      })}
    </div>
  );
};

export default Productos;
