import React from 'react';

const LogicaCampoFormulario = ({ campos, formData, setFormData, accionActiva }) => {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const esRequerido = accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar' && accionActiva !== 'Modificar';

  return (
    <>
      {campos.map(({ name, label, type, options }) => {
        if (type === 'select') {
          return (
            <div key={name} className="campo">
              <label htmlFor={name}>{label}:</label>
              <select id={name} name={name} value={formData[name] || ''}
                onChange={handleInputChange}
                disabled={accionActiva === 'Dar de baja'}
                required={esRequerido}
              >
                <option value="">Seleccione...</option>
                {options.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>
          );
        }

        if (type === 'textarea') {
          return (
            <div key={name} className="campo">
              <label htmlFor={name}>{label}:</label>
              <textarea id={name} name={name} value={formData[name] || ''} onChange={handleInputChange} 
                        rows={4} disabled={accionActiva === 'Dar de baja'} required={esRequerido}
              />
            </div>
          );
        }

        if (name === 'fecha_entrega') {
          return (
            <div key={name} className="campo">
              <label htmlFor={name}>{label}:</label>
              <input
                id={name}
                name={name}
                type={type}
                value={formData[name] || ''}
                onChange={handleInputChange}
                disabled={accionActiva === 'Dar de baja'}
              />
            </div>
          );
        }

        return (
          <div key={name} className="campo">
            <label htmlFor={name}>{label}:</label>
            <input
              id={name}
              name={name}
              type={type}
              value={formData[name] || ''}
              onChange={handleInputChange}
              disabled={accionActiva === 'Dar de baja'}
              required={esRequerido}
            />
          </div>
        );
      })}
    </>
  );
};

export default LogicaCampoFormulario;
