import React, { useState, useEffect } from 'react';
import '../css/agente.css';

const Agente = ({ onSelectCategory, categorias = [] }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [options, setOptions] = useState([]);

  const mainMenu = ['Ver Productos', 'Medios de Pago', 'Horarios de Atenci√≥n'];
  
  // Establecer el men√∫ inicial una sola vez
  useEffect(() => {
    setOptions(mainMenu);
  }, []);

  const toggleMenu = () => {
    // Si estamos cerrando el men√∫, siempre lo reseteamos al men√∫ principal
    if (isOpen) {
      setTimeout(() => setOptions(mainMenu), 200); // Dar tiempo a la animaci√≥n de cierre
    }
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (option) => {
    // Si la opci√≥n es una categor√≠a de producto
    if (categorias.includes(option)) {
      onSelectCategory(option);
      toggleMenu(); // Cierra y resetea el men√∫
      return;
    }

    // Manejo de otras opciones
    switch (option) {
      case 'Ver Productos':
        setOptions([...categorias, 'Volver']);
        break;
      case 'Medios de Pago':
        alert('Para tu comodidad, aceptamos tarjetas de cr√©dito, d√©bito y transferencias por Nequi y Daviplata.');
        toggleMenu();
        break;
      case 'Horarios de Atenci√≥n':
        alert('Atendemos de Lunes a S√°bado, de 9:00 AM a 6:00 PM. ¬°Ser√° un placer ayudarte!');
        toggleMenu();
        break;
      case 'Volver':
        setOptions(mainMenu);
        break;
      default:
        toggleMenu(); // Cierra el men√∫ si hay una opci√≥n desconocida
        break;
    }
  };

  return (
    <div className="fab-container">
      <div className={`fab-menu ${isOpen ? 'open' : ''}`}>
        {options.map((option, index) => (
          <button key={index} onClick={() => handleOptionClick(option)} className="fab-option">
            {option}
          </button>
        ))}
      </div>
      <button onClick={toggleMenu} className="fab-main">
        {isOpen ? '‚úï' : 'üí¨'}
      </button>
    </div>
  );
};

export default Agente;
