import React, { useState } from 'react';

const Contacto = () => {
  const [formulario, setFormulario] = useState({
    nombre: '',
    email: '',
    mensaje: ''
  });

  const handleChange = (e) => {
    setFormulario({
      ...formulario,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Formulario enviado:', formulario);
    // Aquí podrías enviar los datos a un backend o mostrar un mensaje de éxito
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
      <h2>Contacto</h2>
      
      <p>Dirección: Calle 123 #45-67, Ciudad Ejemplo</p>
      <p>Teléfono: (601) 123-4567</p>
      <p>
        <a 
          href="https://wa.me/573001234567" 
          target="_blank" 
          rel="noopener noreferrer"
        >
          WhatsApp: +57 300 123 4567
        </a>
      </p>
      <p>Email: contacto@micatalogo.com</p>

      <hr />

      <h3>Envíanos un mensaje</h3>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label>Nombre:</label><br />
          <input 
            type="text" 
            name="nombre" 
            value={formulario.nombre} 
            onChange={handleChange} 
            required 
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Email:</label><br />
          <input 
            type="email" 
            name="email" 
            value={formulario.email} 
            onChange={handleChange} 
            required 
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Mensaje:</label><br />
          <textarea 
            name="mensaje" 
            value={formulario.mensaje} 
            onChange={handleChange} 
            required 
            style={{ width: '100%', padding: '8px' }}
          ></textarea>
        </div>
        <button type="submit" style={{ padding: '10px 20px' }}>Enviar</button>
      </form>
    </div>
  );
};

export default Contacto;
