// src/logica/logicaLogin.js

const usuariosPorDefecto = [
  { id: 1, usuario: 'sandra', password: '123', rol: 'cliente' },
  { id: 2, usuario: 'sergio', password: '12345', rol: 'proveedor' },
  { id: 3, usuario: 'andres', password: '1234abcd', rol: 'admin' },
];

export const obtenerUsuarios = () => {
  const guardados = localStorage.getItem('usuarios');
  return guardados ? JSON.parse(guardados) : usuariosPorDefecto;
};

export const guardarUsuarios = (usuarios) => {
  localStorage.setItem('usuarios', JSON.stringify(usuarios));
};

export const buscarUsuario = (usuario, password) => {
  const usuarios = obtenerUsuarios();
  return usuarios.find(u => u.usuario === usuario && u.password === password);
};

export const existeUsuario = (usuario) => {
  const usuarios = obtenerUsuarios();
  return usuarios.some(u => u.usuario === usuario);
};

export const agregarUsuario = (nuevoUsuario) => {
  const usuarios = obtenerUsuarios();
  const actualizados = [...usuarios, nuevoUsuario];
  guardarUsuarios(actualizados);
};

export const recuperarPassword = (usuario) => {
  const usuarios = obtenerUsuarios();
  const encontrado = usuarios.find(u => u.usuario === usuario);
  return encontrado ? encontrado.password : null;
};
