import React, { useState } from 'react';
import '../css/login.css';


import {
  buscarUsuario,
  agregarUsuario,
  existeUsuario,
  recuperarPassword,
  obtenerUsuarios
} from '../logica/logicaLogin';

import PanelAdmin from './VistaAPanelAdmin';
import PanelCliente from './VistaAPanelCliente';

const VistaLogin = () => {
  const [form, setForm] = useState({ usuario: '', password: '' });
  const [mensaje, setMensaje] = useState('');
  const [rol, setRol] = useState(null);
  const [mostrarRegistro, setMostrarRegistro] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // DEBUG: mostrar formulario y usuarios disponibles
    console.log('Intento de login con:', form);
    console.log('Usuarios disponibles:', obtenerUsuarios());

    const user = buscarUsuario(form.usuario, form.password);
    if (user) {
      setRol(user.rol);
      setMensaje(`Bienvenido ${user.usuario}, tu rol es ${user.rol}`);
    } else {
      setMensaje('Usuario o contraseña incorrectos');
    }
  };

  const handleRegistro = (e) => {
    e.preventDefault();
    if (existeUsuario(form.usuario)) {
      setMensaje('El usuario ya existe');
    } else {
      const nuevo = {
        id: Date.now(),
        usuario: form.usuario,
        password: form.password,
        rol: 'cliente',
      };
      agregarUsuario(nuevo);
      setMensaje('Registro exitoso. Ahora puedes iniciar sesión.');
      setMostrarRegistro(false);
      setForm({ usuario: '', password: '' });
    }
  };

  const handleRecuperar = () => {
    const pass = recuperarPassword(form.usuario);
    setMensaje(pass ? `Tu contraseña es: ${pass}` : 'Usuario no encontrado');
  };

  // Mostrar panel según rol
  if (rol === 'admin') return <PanelAdmin />;
  if (rol === 'cliente') return <PanelCliente />;

  return (
    <div>
      <h2>{mostrarRegistro ? 'Registro' : 'Login'}</h2>
      <form onSubmit={mostrarRegistro ? handleRegistro : handleSubmit}>
        <input
          name="usuario"
          placeholder="Usuario"
          value={form.usuario}
          onChange={handleChange}
        />
        <input
          name="password"
          type="password"
          placeholder="Contraseña"
          value={form.password}
          onChange={handleChange}
        />
        <button type="submit">
          {mostrarRegistro ? 'Registrar' : 'Ingresar'}
        </button>
      </form>

      {!mostrarRegistro && (
        <div>
          <button onClick={() => setMostrarRegistro(true)}>
            Registrarse
          </button>
          <button onClick={handleRecuperar}>
            ¿Olvidaste tu contraseña?
          </button>
        </div>
      )}

      {mostrarRegistro && (
        <button onClick={() => setMostrarRegistro(false)}>
          Volver al login
        </button>
      )}

      {/* DEBUG: mostrar usuarios guardados */}
      <button
        style={{ marginTop: 10, background: '#eee', border: '1px solid #ccc' }}
        onClick={() => setMensaje(localStorage.getItem('usuarios') || '[]')}
      >
        Mostrar usuarios guardados
      </button>

      {/* DEBUG: restaurar usuarios por defecto */}
      <button
        style={{ marginLeft: 10, marginTop: 10, background: '#fcc', border: '1px solid #c00' }}
        onClick={() => {
          localStorage.removeItem('usuarios');
          window.location.reload();
        }}
      >
        Restaurar usuarios por defecto
      </button>

      {/* Mensaje o JSON */}
      {mensaje && <p style={{ whiteSpace: 'pre-wrap' }}>{mensaje}</p>}
    </div>
  );
};

export default VistaLogin;
