import React, { useState, useEffect } from 'react';
import './App.css';

// Componentes principales de la aplicaciÃ³n
import Carrucel from './componentes/elementos/ElementoCarrucel.js';
import Catalogo from './componentes/vistas/VistaACatalogo.js';
import Footer from './componentes/elementos/ElementoFooter.js';
import ElementoNavProductos from './componentes/elementos/ElementoNavProductos.js';
import BotonWhatsapp from './componentes/elementos/BotonWhatsapp';
import FiltroPrecio from './componentes/elementos/FiltroPrecio';

// Servicios API
import { productosAPI, categoriasAPI, coloresAPI, tallasAPI } from './services/api';

function App() {
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('Todos');
  const [pedido, setPedido] = useState([]);
  const [sortBy, setSortBy] = useState('default');
  const [productos, setProductos] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [colores, setColores] = useState([]);
  const [tallas, setTallas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar datos de la base de datos
  useEffect(() => {
    const cargarDatos = async () => {
      try {
        setLoading(true);

        // Cargar productos, categorÃ­as, colores y tallas en paralelo
        const [productosData, categoriasData, coloresData, tallasData] = await Promise.all([
          productosAPI.getAll(),
          categoriasAPI.getAll(),
          coloresAPI.getAll(),
          tallasAPI.getAll()
        ]);

        setProductos(productosData);
        setCategorias(categoriasData);
        setColores(coloresData);
        setTallas(tallasData);
        setError(null);
      } catch (err) {
        console.error('Error cargando datos:', err);
        setError('Error al cargar los datos. Verifica que el servidor estÃ© corriendo.');
      } finally {
        setLoading(false);
      }
    };

    cargarDatos();
  }, []);

  const handleAgregarAlPedido = (productoAgregado) => {
    setPedido((prev) => {
      // Buscar si ya existe el producto con las mismas caracterÃ­sticas
      const existingIndex = prev.findIndex(
        item => 
          item.id === productoAgregado.id && 
          item.color === productoAgregado.color && 
          item.talla === productoAgregado.talla
      );

      if (existingIndex !== -1) {
        // Si existe, actualizar la cantidad
        const updated = [...prev];
        updated[existingIndex].cantidad += productoAgregado.cantidad;
        alert(`Se actualizÃ³ la cantidad de "${productoAgregado.nombre}" (${productoAgregado.color}) en el pedido.`);
        return updated;
      }

      // Si no existe, lo aÃ±adimos al pedido
      alert(`Se aÃ±adieron ${productoAgregado.cantidad} "${productoAgregado.nombre}" (${productoAgregado.color}) al pedido.`);
      return [...prev, productoAgregado];
    });
  };

  const vaciarPedido = () => {
    setPedido([]);
  };

  // Obtener categorÃ­as Ãºnicas de los productos y agregar "Todos"
  const categoriasUnicas = ['', ...categorias.map(cat => cat.nombre)];

  // Filtrar productos por categorÃ­a
  let productosMostrados = categoriaSeleccionada === 'Todos'
    ? productos
    : productos.filter((p) => {
        // Debug: mostrar quÃ© categorÃ­a tiene cada producto
        console.log(`Producto: ${p.nombre}, CategorÃ­a: ${p.categoria_nombre}, Seleccionada: ${categoriaSeleccionada}`);
        return p.categoria_nombre === categoriaSeleccionada;
      });

  // Ordenar productos
  if (sortBy === 'price-asc') {
    productosMostrados.sort((a, b) => a.precio - b.precio);
  } else if (sortBy === 'price-desc') {
    productosMostrados.sort((a, b) => b.precio - a.precio);
  }

  if (loading) {
    return (
      <div className="App">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          fontSize: '1.2rem'
        }}>
          Cargando catÃ¡logo...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="App">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          flexDirection: 'column',
          gap: '1rem'
        }}>
          <div style={{ color: 'red', fontSize: '1.2rem' }}>{error}</div>
          <button 
            onClick={() => window.location.reload()} 
            style={{ padding: '10px 20px', fontSize: '1rem' }}
          >
            Reintentar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <Carrucel />
        <ElementoNavProductos onSelectCategory={setCategoriaSeleccionada} categorias={categoriasUnicas} />
        <FiltroPrecio setSortBy={setSortBy} />
        <div style={{ 
          position: 'absolute', 
          top: '1rem', 
          right: '1rem',
          zIndex: 1000
        }}>
          <button 
            onClick={() => window.open('/admin', '_blank')}
            style={{
              background: 'linear-gradient(45deg, #00ff41, #00cc33)',
              border: 'none',
              color: 'black',
              padding: '0.5rem 1rem',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600',
              fontSize: '0.9rem',
              boxShadow: '0 4px 15px rgba(0, 255, 65, 0.3)',
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 6px 20px rgba(0, 255, 65, 0.4)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 15px rgba(0, 255, 65, 0.3)';
            }}
          >
            ðŸ”§ AdministraciÃ³n
          </button>
        </div>
      </header>

      <main>
        <Catalogo
          categoriaSeleccionada={categoriaSeleccionada}
          productos={productosMostrados}
          pedido={pedido}
          agregarAlPedido={handleAgregarAlPedido}
          vaciarPedido={vaciarPedido}
          colores={colores}
          tallas={tallas}
        />
      </main>

      <Footer />
      <BotonWhatsapp />
    </div>
  );
}

export default App;
