import React from 'react';
import { ChatBot } from 'react-chatbotify';
import '../css/ChatBotComponent.css';

const ChatBotComponent = () => {
  const steps = [
    {
      id: '1',
      message: '¡Hola! Soy Andrés, tu asistente virtual. ¿En qué puedo ayudarte hoy?',
      trigger: '2',
    },
    {
      id: '2',
      options: [
        { value: 'productos', label: 'Ver productos', trigger: '3' },
        { value: 'pedido', label: 'Hacer un pedido', trigger: '4' },
      ],
    },
    {
      id: '3',
      message: 'Tenemos ropa interior femenina de alta calidad con diseños elegantes. ¿Te gustaría hacer un pedido o tienes otra consulta?',
      trigger: '2',
    },
    {
      id: '4',
      message: 'Perfecto, vamos a hacer tu pedido. ¿Cuál es tu nombre completo?',
      trigger: 'nombre',
    },
    {
      id: 'nombre',
      user: true,
      trigger: 'direccion',
    },
    {
      id: 'direccion',
      message: '¿Me puedes dar la dirección de envío completa?',
      trigger: 'direccionInput',
    },
    {
      id: 'direccionInput',
      user: true,
      trigger: 'telefono',
    },
    {
      id: 'telefono',
      message: '¿Cuál es tu número de teléfono para contacto?',
      trigger: 'telefonoInput',
    },
    {
      id: 'telefonoInput',
      user: true,
      trigger: 'pedidoDetalle',
    },
    {
      id: 'pedidoDetalle',
      message: 'Cuéntame, ¿qué productos quieres pedir? Puedes especificar cantidad y modelo.',
      trigger: 'pedidoInput',
    },
    {
      id: 'pedidoInput',
      user: true,
      trigger: 'confirmacion',
    },
    {
      id: 'confirmacion',
      message: 'Gracias por la información. ¿Cómo deseas pagar? (Efectivo, tarjeta, transferencia)',
      trigger: 'pago',
    },
    {
      id: 'pago',
      user: true,
      trigger: 'fin',
    },
    {
      id: 'fin',
      message: 'Perfecto, tu pedido está confirmado. Te contactaremos pronto para coordinar el pago y envío. ¿Necesitas algo más?',
      trigger: 'masAyuda',
    },
    {
      id: 'masAyuda',
      options: [
        { value: 'si', label: 'Sí', trigger: '2' },
        { value: 'no', label: 'No, gracias', trigger: 'saludoFinal' },
      ],
    },
    {
      id: 'saludoFinal',
      message: '¡Gracias por tu visita! Que tengas un excelente día.',
      end: true,
    },
  ];

  return <ChatBot steps={steps} />;
};

export default ChatBotComponent;
