import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div
      style={{
        position: 'relative',
        border: '1px solid #ccc',
        borderRadius: 8,
        padding: 0,
        margin: 8,
        width: 250,
        overflow: 'hidden',
        cursor: 'pointer',
      }}
      onClick={() => setShowInfo(!showInfo)} // Mostrar/ocultar al hacer clic
      onMouseEnter={() => setShowInfo(true)} // Mostrar al hacer hover
      onMouseLeave={() => setShowInfo(false)} // Ocultar al salir
    >
      <img src={imagen} alt={nombre} style={{ width: '100%', display: 'block' }} />

      {/* Capa que aparece encima con la info */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.7)',
          color: 'white',
          padding: 16,
          opacity: showInfo ? 1 : 0,
          transition: 'opacity 0.3s',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
        }}
      >
        <h3>{nombre}</h3>
        <p>{descripcion}</p>
        <p><strong>Precio:</strong> ${precio}</p>
        {colores && <p><strong>Colores:</strong> {colores.join(', ')}</p>}
        {tallas && <p><strong>Tallas:</strong> {tallas.join(', ')}</p>}

        <button
          onClick={(e) => {
            e.stopPropagation(); // Evita que cierre al hacer clic en el botón
            onAgregar();
          }}
          style={{
            marginTop: '10px',
            padding: '8px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
          }}
        >
          Agregar al carrito
        </button>
      </div>
    </div>
  );
};

export default Tarjeta;
