import React from 'react';

const Ventas = ({ accionActiva, formData, setFormData }) => {
  const campos = [
    { name: 'id', label: 'ID Venta', type: 'number' },
    { name: 'producto', label: 'Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'precio_unitario', label: 'Precio Unitario', type: 'number' },
    { name: 'fecha_venta', label: 'Fecha de Venta', type: 'datetime-local' },
    { name: 'cliente', label: 'Cliente', type: 'text' },
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <>
      {campos.map(({ name, label, type }) => (
        <div key={name} style={{ marginBottom: 12 }}>
          <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
            {label}:
          </label>
          <input
            id={name}
            name={name}
            type={type}
            value={formData[name] || ''}
            onChange={handleInputChange}
            required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
            disabled={accionActiva === 'Dar de baja'}
            style={{
              width: '100%',
              padding: '0.4rem',
              borderRadius: 4,
              border: '1px solid #ccc',
            }}
          />
        </div>
      ))}
    </>
  );
};

export default Ventas;
