import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div
      style={{
        border: '1px solid #ccc',
        borderRadius: 8,
        padding: 16,
        margin: 8,
        width: 250,
        cursor: 'pointer',
        userSelect: 'none',
      }}
    >
      <img
        src={imagen}
        alt={nombre}
        style={{ width: '100%', borderRadius: 4 }}
        onClick={() => setShowInfo(!showInfo)}
      />

      {/* Info desplegable */}
      <div
        style={{
          maxHeight: showInfo ? '500px' : '0',
          overflow: 'hidden',
          transition: 'max-height 0.4s ease',
          marginTop: showInfo ? 16 : 0,
        }}
      >
        {showInfo && (
          <>
            <h3>{nombre}</h3>
            <p>{descripcion}</p>
            <p><strong>Precio:</strong> ${precio}</p>
            {colores && <p><strong>Colores:</strong> {colores.join(', ')}</p>}
            {tallas && <p><strong>Tallas:</strong> {tallas.join(', ')}</p>}

            <button
              onClick={(e) => {
                e.stopPropagation(); // Evita que se cierre al hacer clic
                onAgregar();
              }}
              style={{
                marginTop: '10px',
                padding: '8px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
              }}
            >
              Agregar al carrito
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Tarjeta;
