import React, { useState } from 'react';
import '../css/login.css';

const usuarios = [
  { id: 1, usuario: 'cliente1', password: '123', rol: 'cliente' },
  { id: 2, usuario: 'proveedor1', password: '123', rol: 'proveedor' },
  { id: 3, usuario: 'admin1', password: '1234', rol: 'admin' },
];

const Login = () => {
  const [form, setForm] = useState({ usuario: '', password: '' });
  const [mensaje, setMensaje] = useState('');
  const [rol, setRol] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const user = usuarios.find(
      u => u.usuario === form.usuario && u.password === form.password
    );
    if (user) {
      setRol(user.rol);
      setMensaje(`Bienvenido ${user.usuario}, tu rol es ${user.rol}`);
    } else {
      setMensaje('Usuario o contraseña incorrectos');
    }
  };

  if (rol) {
    // Aquí rediriges o muestras según el rol
    return <div>Panel para {rol}</div>;
  }

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input name="usuario" placeholder="Usuario" value={form.usuario} onChange={handleChange} />
        <input name="password" placeholder="Contraseña" type="password" value={form.password} onChange={handleChange} />
        <button type="submit">Ingresar</button>
      </form>
      {mensaje && <p>{mensaje}</p>}
    </div>
  );
};

export default Login;
