import React, { useState } from 'react';
import Tarjeta from '../elementos/Tarjeta';
import Categorias from '../elementos/Categorias';
import Carrito from '../elementos/Carrito';
import { productos } from '../elementos/productos';
import '../css/catalogo.css';

const Catalogo = () => {
  const [carrito, setCarrito] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('');

  const handleAgregarAlCarrito = (producto) => {
    setCarrito((prev) => [...prev, producto]);
  };

  const productosFiltrados = categoriaSeleccionada
    ? productos.filter((p) => p.categoria === categoriaSeleccionada)
    : productos;

  // Alias para poder usar con mayúscula en JSX
  c
  return (
    <>
      <Navproductos />
      <div className="catalogo-container">
        <Categorias onSeleccionar={setCategoriaSeleccionada} />

        <div className="catalogo-productos">
          <h2>Catálogo</h2>
          <div className="catalogo-grid">
            {productosFiltrados.map((producto) => (
              <Tarjeta
                key={producto.id}
                imagen={producto.imagen}
                nombre={producto.nombre}
                descripcion={producto.descripcion}
                precio={producto.precio}
                colores={producto.colores}
                tallas={producto.tallas}
                onAgregar={() => handleAgregarAlCarrito(producto)}
              />
            ))}
          </div>
        </div>

        <Carrito items={carrito} />
      </div>
    </>
  );
};

export default Catalogo;
