import React from 'react';

const VistaAInventario = ({ accionActiva, formData, setFormData }) => {
  const campos = [
    { name: 'id', label: 'ID', type: 'number' },
    { name: 'nombre', label: 'Nombre del Producto', type: 'text' },
    { name: 'cantidad', label: 'Cantidad', type: 'number' },
    { name: 'numero_factura_orden', label: 'Número Factura u Orden', type: 'text' },
    { name: 'ubicacion', label: 'Ubicación', type: 'text' },
    { 
      name: 'custodia', 
      label: 'Custodia', 
      type: 'select', 
      options: ['Área A', 'Área B', 'Área C'] 
    },
    { 
      name: 'proveedor', 
      label: 'Proveedor', 
      type: 'select', 
      options: ['Proveedor 1', 'Proveedor 2', 'Proveedor 3'] 
    },
    { name: 'fecha_ingreso', label: 'Fecha de Ingreso', type: 'datetime-local' },
    { name: 'fecha_entrega', label: 'Fecha de Entrega', type: 'datetime-local' },
    { name: 'descripcion', label: 'Descripción', type: 'textarea' },
  ];

  const manejarCambioEntrada = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div>
      {campos.map(({ name, label, type, options }) => {
        const esFechaEntrega = name === 'fecha_entrega';
        const esDesactivado = accionActiva === 'Dar de baja';
        const esRequerido = 
          !esDesactivado && 
          accionActiva !== 'Buscar' && 
          !esFechaEntrega; // 👈 Aquí se evita que "fecha_entrega" sea requerido

        if (type === 'select') {
          return (
            <div key={name} style={{ marginBottom: 12 }}>
              <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                {label}:
              </label>
              <select
                id={name}
                name={name}
                value={formData[name] || ''}
                onChange={manejarCambioEntrada}
                disabled={esDesactivado}
                required={esRequerido}
                style={{
                  width: '100%',
                  padding: '0.4rem',
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              >
                <option value="">Seleccione...</option>
                {options.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>
          );
        } else if (type === 'textarea') {
          return (
            <div key={name} style={{ marginBottom: 12 }}>
              <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                {label}:
              </label>
              <textarea
                id={name}
                name={name}
                value={formData[name] || ''}
                onChange={manejarCambioEntrada}
                rows={4}
                disabled={esDesactivado}
                required={esRequerido}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              />
            </div>
          );
        } else {
          return (
            <div key={name} style={{ marginBottom: 12 }}>
              <label htmlFor={name} style={{ display: 'block', marginBottom: 4 }}>
                {label}:
              </label>
              <input
                id={name}
                name={name}
                type={type}
                value={formData[name] || ''}
                onChange={manejarCambioEntrada}
                disabled={esDesactivado}
                required={esRequerido}
                style={{
                  width: '100%',
                  padding: '0.4rem',
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              />
            </div>
          );
        }
      })}
    </div>
  );
};

export default VistaAInventario;
