import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div style={{ 
      display: 'flex', 
      border: '1px solid #ccc', 
      borderRadius: 8, 
      padding: 16, 
      margin: 8, 
      width: 500, // más ancho para dos columnas
      boxSizing: 'border-box',
    }}>
      {/* Columna imagen */}
      <div style={{ flex: '1 1 40%', marginRight: 16 }}>
        <img 
          src={imagen} 
          alt={nombre} 
          style={{ width: '100%', borderRadius: 4, cursor: 'pointer' }}
          onClick={() => setShowInfo(!showInfo)} 
        />
      </div>

      {/* Columna info + carrito */}
      <div style={{ flex: '1 1 60%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
        <div>
          <h3>{nombre}</h3>
          <p>{descripcion}</p>
          <p><strong>Precio:</strong> ${precio}</p>
          {colores && <p><strong>Colores:</strong> {colores.join(', ')}</p>}
          {tallas && <p><strong>Tallas:</strong> {tallas.join(', ')}</p>}

          {/* Información adicional desplegable */}
          {showInfo && (
            <div style={{ 
              marginTop: 10, 
              padding: 10, 
              backgroundColor: '#f1f1f1', 
              borderRadius: 4, 
              boxShadow: '0 2px 6px rgba(0,0,0,0.1)'
            }}>
              <p><em>Información adicional que aparece al hacer clic en la imagen.</em></p>
              {/* Aquí más detalles si quieres */}
            </div>
          )}
        </div>

        {/* Botón carrito abajo */}
        <button 
          onClick={onAgregar} 
          style={{ 
            marginTop: 20, 
            padding: '12px', 
            backgroundColor: '#007bff', 
            color: 'white', 
            border: 'none', 
            borderRadius: 4, 
            cursor: 'pointer' 
          }}
        >
          Agregar al carrito
        </button>
      </div>
    </div>
  );
};

export default Tarjeta;
