import React, { useState, useRef, useEffect } from 'react';
import '../css/agente.css'; 
import avatar from '../img/logo.jpg'; // Importamos el avatar

const Agente = ({ onSelectCategory, categorias = [] }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  
  const initialMessage = { 
    from: 'bot', 
    text: 'Â¡Hola! Soy Daniel, tu asesor de estilo personal. He seleccionado lo mejor para ti. Â¿Listo/a para encontrar prendas increÃ­bles que te harÃ¡n destacar?', 
    options: ['Â¡MuÃ©strame los productos!', 'Â¿CÃ³mo puedo pagar?', 'Nuestros horarios'] 
  };

  // Cargar el mensaje inicial
  useEffect(() => {
    setMessages([initialMessage]);
  }, []);

  const chatBodyRef = useRef(null);

  // Scroll automÃ¡tico
  useEffect(() => {
    if (chatBodyRef.current) {
      chatBodyRef.current.scrollTop = chatBodyRef.current.scrollHeight;
    }
  }, [messages]);

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionClick = (option) => {
    const userMessage = { from: 'user', text: option };
    let botResponse;

    if (categorias.includes(option)) {
      onSelectCategory(option);
      botResponse = { from: 'bot', text: `Â¡Excelente elecciÃ³n! La lÃ­nea de ${option} tiene piezas espectaculares. Ya puedes verlas. Â¿QuÃ© te parece si aÃ±ades tu favorita al carrito antes de que se agote?`, options: initialMessage.options };
    } else {
      switch (option) {
        case 'Â¡MuÃ©strame los productos!':
          botResponse = { from: 'bot', text: 'Â¡Vamos allÃ¡! Cada categorÃ­a tiene un estilo Ãºnico. Â¿CuÃ¡l te llama mÃ¡s la atenciÃ³n para empezar a descubrir tus nuevos favoritos?', options: [...categorias, 'Volver'] };
          break;
        case 'Â¿CÃ³mo puedo pagar?':
          botResponse = { from: 'bot', text: 'Â¡Hicimos que pagar sea muy fÃ¡cil y seguro! Aceptamos todas las tarjetas, Nequi y Daviplata. Â¿CuÃ¡l producto te empacamos?', options: ['Â¡MuÃ©strame los productos!', 'Volver'] };
          break;
        case 'Nuestros horarios':
          botResponse = { from: 'bot', text: 'Estamos para ti de Lunes a SÃ¡bado, de 9 AM a 6 PM. Â¡Siempre es un buen momento para darte un gusto!', options: ['Volver'] };
          break;
        case 'Volver':
          botResponse = initialMessage;
          break;
        default:
          botResponse = { from: 'bot', text: 'No estoy seguro de entenderte. Â¿QuÃ© te parece si te muestro nuestros productos mÃ¡s vendidos para inspirarte?', options: initialMessage.options };
          break;
      }
    }

    setMessages(prev => [...prev.filter(m => m.from === 'user' || m.text !== option), userMessage, botResponse]);
  };

  const currentOptions = messages[messages.length - 1]?.options || [];

  return (
    <div className="chatbot-container">
      <div className={`chat-window ${isOpen ? 'open' : ''}`}>
        <div className="chat-header">
          <img src={avatar} alt="Avatar de Daniel" className="chat-avatar" />
          <h2>Daniel, tu Asesor</h2>
          <button onClick={toggleChat} className="close-btn">&times;</button>
        </div>
        <div className="chat-body" ref={chatBodyRef}>
          {messages.map((msg, index) => (
            <div key={index} className={`message-container ${msg.from}`}>
              <div className={`message ${msg.from}`}>
                {msg.text}
              </div>
            </div>
          ))}
        </div>
        <div className="chat-footer">
          {currentOptions.map((option, index) => (
            <button key={index} onClick={() => handleOptionClick(option)} className="option-btn">
              {option}
            </button>
          ))}
        </div>
      </div>
      <button onClick={toggleChat} className="chat-toggle-btn">
        <span>ðŸ’¬</span>
      </button>
    </div>
  );
};

export default Agente;
