import React from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas }) => {
  return (
    <div style={{
      border: '1px solid #ccc',
      borderRadius: '8px',
      padding: '16px',
      margin: '8px',
      width: '250px'
    }}>
      <img src={imagen} alt={nombre} style={{ width: '100%', borderRadius: '4px' }} />
      <h3>{nombre}</h3>
      <p>{descripcion}</p>
      <p><strong>Precio:</strong> ${precio}</p>
      {colores && (
        <p><strong>Colores:</strong> {colores.join(', ')}</p>
      )}
      {tallas && (
        <p><strong>Tallas:</strong> {tallas.join(', ')}</p>
      )}
    </div>
  );
};

export default Tarjeta;
