import React from 'react';

const VistaAInventario = ({ accionActiva, formData, setFormData }) => {
  const campos = [
    { nombre: 'id', etiqueta: 'ID', tipo: 'number' },
    { nombre: 'nombre', etiqueta: 'Nombre del Producto', tipo: 'text' },
    { nombre: 'cantidad', etiqueta: 'Cantidad', tipo: 'number' },
    { nombre: 'numero_factura_orden', etiqueta: 'Número Factura u Orden', tipo: 'text' },
    { nombre: 'ubicacion', etiqueta: 'Ubicación', tipo: 'text' },
    { 
      nombre: 'custodia', 
      etiqueta: 'Custodia', 
      tipo: 'select', 
      opciones: ['Área A', 'Área B', 'Área C'] 
    },
    { 
      nombre: 'proveedor', 
      etiqueta: 'Proveedor', 
      tipo: 'select', 
      opciones: ['Proveedor 1', 'Proveedor 2', 'Proveedor 3'] 
    },
    { nombre: 'fecha_ingreso', etiqueta: 'Fecha de Ingreso', tipo: 'datetime-local' },
    { nombre: 'fecha_entrega', etiqueta: 'Fecha de Entrega', tipo: 'datetime-local' },
    { nombre: 'descripcion', etiqueta: 'Descripción', tipo: 'textarea' },
  ];

  const manejarCambioInput = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  return (
    <div>
      {campos.map(({ nombre, etiqueta, tipo, opciones }) => {
        if (tipo === 'select') {
          return (
            <div key={nombre} style={{ marginBottom: 12 }}>
              <label htmlFor={nombre} style={{ display: 'block', marginBottom: 4 }}>
                {etiqueta}:
              </label>
              <select
                id={nombre}
                name={nombre}
                value={formData[nombre] || ''}
                onChange={manejarCambioInput}
                disabled={accionActiva === 'Dar de baja'}
                required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                style={{
                  width: '100%',
                  padding: '0.4rem',
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              >
                <option value="">Seleccione...</option>
                {opciones.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>
          );
        } else if (tipo === 'textarea') {
          return (
            <div key={nombre} style={{ marginBottom: 12 }}>
              <label htmlFor={nombre} style={{ display: 'block', marginBottom: 4 }}>
                {etiqueta}:
              </label>
              <textarea
                id={nombre}
                name={nombre}
                value={formData[nombre] || ''}
                onChange={manejarCambioInput}
                rows={4}
                disabled={accionActiva === 'Dar de baja'}
                required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              />
            </div>
          );
        } else {
          return (
            <div key={nombre} style={{ marginBottom: 12 }}>
              <label htmlFor={nombre} style={{ display: 'block', marginBottom: 4 }}>
                {etiqueta}:
              </label>
              <input
                id={nombre}
                name={nombre}
                type={tipo}
                value={formData[nombre] || ''}
                onChange={manejarCambioInput}
                disabled={accionActiva === 'Dar de baja'}
                required={accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar'}
                style={{
                  width: '100%',
                  padding: '0.4rem',
                  borderRadius: 4,
                  border: '1px solid #ccc',
                }}
              />
            </div>
          );
        }
      })}
    </div>
  );
};

export default VistaAInventario;
