// Archivo: VistaCampoFormulario.js
import React from 'react';
import { obtenerCamposPorAccion } from './LogicaCampoFormulario';

const VistaCampoFormulario = ({ accionActiva, formData, setFormData }) => {
  const campos = obtenerCamposPorAccion(accionActiva);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div>
      {campos.map(({ name, label, type, options, required }) => {
        if (type === 'select') {
          return (
            <div key={name} className="campo">
              <label htmlFor={name}>{label}:</label>
              <select
                id={name}
                name={name}
                value={formData[name] || ''}
                onChange={handleInputChange}
                disabled={accionActiva === 'Dar de baja'}
                required={required ?? (accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar')}
              >
                <option value="">Seleccione...</option>
                {options.map(opt => (
                  <option key={opt} value={opt}>{opt}</option>
                ))}
              </select>
            </div>
          );
        } else if (type === 'textarea') {
          return (
            <div key={name} className="campo">
              <label htmlFor={name}>{label}:</label>
              <textarea
                id={name}
                name={name}
                value={formData[name] || ''}
                onChange={handleInputChange}
                rows={4}
                disabled={accionActiva === 'Dar de baja'}
                required={required ?? (accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar')}
              />
            </div>
          );
        } else {
          return (
            <div key={name} className="campo">
              <label htmlFor={name}>{label}:</label>
              <input
                id={name}
                name={name}
                type={type}
                value={formData[name] || ''}
                onChange={handleInputChange}
                disabled={accionActiva === 'Dar de baja'}
                required={required ?? (accionActiva !== 'Dar de baja' && accionActiva !== 'Buscar')}
              />
            </div>
          );
        }
      })}
    </div>
  );
};

export default VistaCampoFormulario;
