import React, { useState, useEffect } from 'react';
import './App.css';

// Componentes principales de la aplicaciÃ³n
import Carrucel from './componentes/elementos/ElementoCarrucel.js';
import Catalogo from './componentes/vistas/VistaACatalogo.js';
import Footer from './componentes/elementos/ElementoFooter.js';
import ElementoNavProductos from './componentes/elementos/ElementoNavProductos.js';
import BotonWhatsapp from './componentes/elementos/BotonWhatsapp';
import FiltroPrecio from './componentes/elementos/FiltroPrecio';

// Servicios API
import { productosAPI, categoriasAPI } from './services/api';

function App() {
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('Todos');
  const [carrito, setCarrito] = useState([]);
  const [sortBy, setSortBy] = useState('default');
  const [productos, setProductos] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar datos de la base de datos
  useEffect(() => {
    const cargarDatos = async () => {
      try {
        setLoading(true);

        // Cargar productos y categorÃ­as en paralelo
        const [productosData, categoriasData] = await Promise.all([
          productosAPI.getAll(),
          categoriasAPI.getAll()
        ]);

        setProductos(productosData);
        setCategorias(categoriasData);
        setError(null);
      } catch (err) {
        console.error('Error cargando datos:', err);
        setError('Error al cargar los datos. Verifica que el servidor estÃ© corriendo.');
      } finally {
        setLoading(false);
      }
    };

    cargarDatos();
  }, []);

  const handleAgregarAlCarrito = (productoAgregado) => {
    setCarrito((prev) => {
      // Un producto es Ãºnico por su ID y su color
      const productoExistente = prev.find(
        (item) => item.id === productoAgregado.id && item.color === productoAgregado.color
      );

      if (productoExistente) {
        // Si ya existe, actualizamos su cantidad
        return prev.map((item) =>
          item.id === productoAgregado.id && item.color === productoAgregado.color
            ? { ...item, cantidad: item.cantidad + productoAgregado.cantidad }
            : item
        );
      } else {
        // Si no existe, lo aÃ±adimos al carrito
        return [...prev, productoAgregado];
      }
    });
    alert(`Se aÃ±adieron ${productoAgregado.cantidad} "${productoAgregado.nombre}" (${productoAgregado.color}) al carrito.`);
  };

  const vaciarCarrito = () => {
    setCarrito([]);
  };

  // Obtener categorÃ­as Ãºnicas de los productos y agregar "Todos"
  const categoriasUnicas = ['Todos', ...categorias.map(cat => cat.nombre)];

  // Filtrar productos por categorÃ­a
  let productosMostrados = categoriaSeleccionada === 'Todos'
    ? productos
    : productos.filter((p) => p.categoria_nombre === categoriaSeleccionada);

  // Ordenar productos
  if (sortBy === 'price-asc') {
    productosMostrados.sort((a, b) => a.precio - b.precio);
  } else if (sortBy === 'price-desc') {
    productosMostrados.sort((a, b) => b.precio - a.precio);
  }

  if (loading) {
    return (
      <div className="App">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          fontSize: '1.2rem'
        }}>
          Cargando catÃ¡logo...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="App">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          height: '100vh',
          flexDirection: 'column',
          gap: '1rem'
        }}>
          <div style={{ color: 'red', fontSize: '1.2rem' }}>{error}</div>
          <button 
            onClick={() => window.location.reload()} 
            style={{ padding: '10px 20px', fontSize: '1rem' }}
          >
            Reintentar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="App-header">
        <Carrucel />
        <ElementoNavProductos onSelectCategory={setCategoriaSeleccionada} categorias={categoriasUnicas} />
        <FiltroPrecio setSortBy={setSortBy} />
        <div style={{ 
          position: 'absolute', 
          top: '1rem', 
          right: '1rem',
          zIndex: 1000
        }}>
          <button 
            onClick={() => window.open('/admin', '_blank')}
            style={{
              background: 'linear-gradient(45deg, #00ff41, #00cc33)',
              border: 'none',
              color: 'black',
              padding: '0.5rem 1rem',
              borderRadius: '8px',
              cursor: 'pointer',
              fontWeight: '600',
              fontSize: '0.9rem',
              boxShadow: '0 4px 15px rgba(0, 255, 65, 0.3)',
              transition: 'all 0.3s ease'
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 6px 20px rgba(0, 255, 65, 0.4)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 4px 15px rgba(0, 255, 65, 0.3)';
            }}
          >
            ðŸ”§ AdministraciÃ³n
          </button>
        </div>
      </header>

      <main>
        <Catalogo
          categoriaSeleccionada={categoriaSeleccionada}
          productos={productosMostrados}
          carrito={carrito}
          agregarAlCarrito={handleAgregarAlCarrito}
          vaciarCarrito={vaciarCarrito}
        />
      </main>

      <Footer />
      <BotonWhatsapp />
    </div>
  );
}

export default App;
