import React, { useState } from 'react';

const Tarjeta = ({ imagen, nombre, descripcion, precio, colores, tallas, onAgregar }) => {
  const [showOverlay, setShowOverlay] = useState(false);

  return (
    <div
      style={{
        position: 'relative',
        border: '1px solid #ccc',
        borderRadius: 8,
        padding: 16,
        margin: 8,
        width: 300,
        display: 'flex',
        alignItems: 'center',
        gap: '16px',
        userSelect: 'none',
      }}
    >
      {/* Imagen con clic para toggle overlay */}
      <div
        style={{ position: 'relative', flexShrink: 0, cursor: 'pointer' }}
        onClick={() => setShowOverlay(!showOverlay)}
      >
        <img
          src={imagen}
          alt={nombre}
          style={{ width: 150, borderRadius: 4, display: 'block' }}
        />
        {/* Overlay semi-transparente */}
        {showOverlay && (
          <div
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              backgroundColor: 'rgba(0, 0, 0, 0.6)',
              color: 'white',
              padding: 10,
              boxSizing: 'border-box',
              borderRadius: 4,
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'center',
              fontSize: 12,
              lineHeight: '1.2',
              pointerEvents: 'none', // que no interfiera con clics al botón
            }}
          >
            <strong>{nombre}</strong>
            <span>{descripcion}</span>
            <span>Precio: ${precio}</span>
            {colores && <span>Colores: {colores.join(', ')}</span>}
            {tallas && <span>Tallas: {tallas.join(', ')}</span>}
          </div>
        )}
      </div>

      {/* Botón fijo */}
      <button
        onClick={onAgregar}
        style={{
          padding: '10px 16px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: 4,
          cursor: 'pointer',
          flexShrink: 0,
        }}
      >
        Agregar al carrito
      </button>
    </div>
  );
};

export default Tarjeta;
